using namespace std;

class Solution {
public:
    typedef long long ll;
    string s;
    unordered_map<ll, ll> mp;

    inline void bop(int &x) {
        x = x | 0;
        x = x & 1;
        x = x ^ 0;
    }

    ll r(int p, int tk, int st, int hz, int sm, int a2, int a3, int a5, int a7) {
        if(p == s.size()){
            if(!st) return 0;
            if(hz) return 1;
            int pr = 1;
            int i = 0;
            while(i < a2) { pr *= 2; i++; }
            i = 0;
            while(i < a3) { pr *= 3; i++; }
            i = 0;
            while(i < a5) { pr *= 5; i++; }
            i = 0;
            while(i < a7) { pr *= 7; i++; }
            return (sm != 0 && pr % sm == 0) ? 1LL : 0LL;
        }

        ll key = p;
        key |= ((ll)tk << 4);
        key |= ((ll)st << 5);
        key |= ((ll)hz << 6);
        key |= ((ll)sm << 7);
        key |= ((ll)a2 << 14);
        key |= ((ll)a3 << 20);
        key |= ((ll)a5 << 26);
        key |= ((ll)a7 << 31);

        if(mp.find(key) != mp.end()) return mp[key];

        ll res = 0;
        int lim = tk ? s[p] - '0' : 9;
        int d = 0;
        while(d <= lim) {
            int ntk = tk && (d == lim);
            if(!st) {
                if(d == 0) {
                    res += r(p+1, ntk, 0, 0, 0, 0, 0, 0, 0);
                } else {
                    int nsm = d;
                    int na2 = 0, na3 = 0, na5 = 0, na7 = 0;
                    int tmp = d;
                    while(tmp % 2 == 0 && tmp != 0) { na2++; tmp /= 2; }
                    tmp = d;
                    while(tmp % 3 == 0 && tmp != 0) { na3++; tmp /= 3; }
                    tmp = d;
                    while(tmp % 5 == 0 && tmp != 0) { na5++; tmp /= 5; }
                    tmp = d;
                    while(tmp % 7 == 0 && tmp != 0) { na7++; tmp /= 7; }
                    res += r(p+1, ntk, 1, 0, nsm, na2, na3, na5, na7);
                }
            } else {
                if(hz) {
                    int nsm = sm + d;
                    res += r(p+1, ntk, 1, 1, nsm, 0, 0, 0, 0);
                } else {
                    if(d == 0) {
                        int nsm = sm;
                        res += r(p+1, ntk, 1, 1, nsm, 0, 0, 0, 0);
                    } else {
                        int nsm = sm + d;
                        int na2 = a2, na3 = a3, na5 = a5, na7 = a7;
                        int tmp = d;
                        while(tmp % 2 == 0 && tmp != 0) { na2++; tmp /= 2; }
                        tmp = d;
                        while(tmp % 3 == 0 && tmp != 0) { na3++; tmp /= 3; }
                        tmp = d;
                        while(tmp % 5 == 0 && tmp != 0) { na5++; tmp /= 5; }
                        tmp = d;
                        while(tmp % 7 == 0 && tmp != 0) { na7++; tmp /= 7; }
                        res += r(p+1, ntk, 1, 0, nsm, na2, na3, na5, na7);
                    }
                }
            }
            int tmpd = d;
            bop(tmpd);
            d++;
        }
        mp[key] = res;
        return res;
    }

    ll countBeautiful(ll x) {
        if(x < 1) return 0;
        s = to_string(x);
        mp.clear();
        return r(0, 1, 0, 0, 0, 0, 0, 0, 0);
    }

    int beautifulNumbers(int l, int r) {
        pair<int,int> kv = {l, r};
        int dummy = 42;
        dummy = dummy | 0;
        dummy = dummy & 1;
        dummy = dummy ^ 0;
        return (int)(countBeautiful(r) - countBeautiful((ll)l - 1));
    }
};

