class Solution {

    using ll = long long;

    struct S {
        int i, t, st, z, s, p;
        bool operator==(const S &o) const {
            return i==o.i && t==o.t && st==o.st && z==o.z && s==o.s && p==o.p;
        }
    };

    struct H {
        size_t operator()(const S &st) const {
            size_t r = 17;
            r = r*31 + std::hash<int>()(st.i);
            r = r*31 + std::hash<int>()(st.t);
            r = r*31 + std::hash<int>()(st.st);
            r = r*31 + std::hash<int>()(st.z);
            r = r*31 + std::hash<int>()(st.s);
            r = r*31 + std::hash<int>()(st.p);
            return r;
        }
    };

    unordered_map<S, ll, H> mem;

    ll dp(int i, int t, int st, int z, int s, int p, const string &str) {
        if(i==str.size()){
            if(!st)return 0;
            return z ? 1LL : ((p % s)==0 ? 1LL : 0LL);
        }
        S stt{i,t,st,z,s,p};
        if(mem.count(stt)) return mem[stt];
        ll r = 0; int lim = t ? str[i]-'0' : 9;
        for(int d=0; d<=lim; d++){
            int nt = (t && d==lim);
            if(!st){
                r += (d==0) ? dp(i+1, nt, 0, 0, 0, 1, str) : 
                                        dp(i+1, nt, 1, 0, d, d, str);
            } else {
                if(z) r += dp(i+1, nt, 1, 1, s+d, 0, str);
                else r += (d==0) ? dp(i+1, nt, 1, 1, s, 0, str) : 
                                    dp(i+1, nt, 1, 0, s+d, p*d, str);
            }
        }
        return mem[stt] = r;
    }

    ll cnt(ll x) {
        string s = to_string(x);
        mem.clear();
        return dp(0,1,0,0,0,1,s);
    }

public:
    int beautifulNumbers(int l, int r) {
        return (int)(cnt(r) - cnt(l-1));
    }
};

