#include <vector>
#include <string>
#include <unordered_map>
using namespace std;

class Solution {
public:
    long long dp(int pos, int tight, int started, int zero, int s, long long p, const vector<int>& digs, unordered_map<string, long long>& memo) {
        if(pos == digs.size()){
            if(!started) return 0;
            if(zero) return 1;
            if(s > 0 && p % s == 0) return 1;
            return 0;
        }
        string key = to_string(pos) + "_" + to_string(tight) + "_" + to_string(started) + "_" + to_string(zero) + "_" + to_string(s) + "_" + to_string(p);
        if(memo.find(key) != memo.end()){
            return memo[key];
        }
        long long ans = 0;
        int lim = (tight == 1 ? digs[pos] : 9);
        for (int d = 0; d <= lim; d++){
            int nt = (tight == 1 && d == lim) ? 1 : 0;
            int ns = s, nsd = started, nz = zero;
            long long np = p;
            if(!started){
                if(d != 0){
                    nsd = 1;
                    ns = d;
                    np = d;
                }
            } else {
                if(!zero){
                    if(d == 0){
                        nz = 1;
                        ns = 0;
                        np = 0;
                    } else {
                        ns = s + d;
                        np = p * d;
                    }
                }
            }
            ans += dp(pos + 1, nt, nsd, nz, ns, np, digs, memo);
        }
        memo[key] = ans;
        return ans;
    }

    long long countUpTo(long long X) {
        if(X < 1) return 0;
        string str = to_string(X);
        vector<int> digs;
        for(char c : str)
            digs.push_back(c - '0');
        unordered_map<string, long long> memo;
        return dp(0, 1, 0, 0, 0, 1, digs, memo);
    }

    int beautifulNumbers(int l, int r) {
        long long ans = countUpTo(r) - countUpTo(l - 1);
        return (int)ans;
    }
};

