#include <stdlib.h>
#include <stdio.h>
#include <string.h>

typedef unsigned long long ULL;

typedef struct {
    int pos;
    int tight;
    int started;
    int hasZero;
    int sum;
    ULL prod;
} Key;

typedef struct {
    Key key;
    long long value;
    int used;
} Entry;

#define HASH_SIZE (1 << 17)

static Entry memo[HASH_SIZE];

static inline unsigned int hashKey(const Key* k) {
    unsigned int h = 0;
    h = k->pos;
    h = h * 31 + k->tight;
    h = h * 31 + k->started;
    h = h * 31 + k->hasZero;
    h = h * 31 + k->sum;
    h = h * 31 + (unsigned int)(k->prod & 0xffffffffULL);
    h = h * 31 + (unsigned int)((k->prod >> 32) & 0xffffffffULL);
    return h;
}

static inline int keysEqual(const Key* a, const Key* b) {
    return a->pos == b->pos && a->tight == b->tight && a->started == b->started &&
           a->hasZero == b->hasZero && a->sum == b->sum && a->prod == b->prod;
}

long long dp(int pos, int tight, int started, int hasZero, int sum, ULL prod, int n, int* digits) {
    if (pos == n) {
        if (!started) return 0;
        if (hasZero) return 1;
        return (prod % sum == 0) ? 1 : 0;
    }
    Key key = {pos, tight, started, hasZero, sum, prod};
    unsigned int h = hashKey(&key) & (HASH_SIZE - 1);
    while (memo[h].used) {
        if (keysEqual(&memo[h].key, &key))
            return memo[h].value;
        h = (h + 1) & (HASH_SIZE - 1);
    }
    long long res = 0;
    int limit = tight ? digits[pos] : 9;
    for (int dig = 0; dig <= limit; dig++) {
        int nstarted = started;
        int nhasZero = hasZero;
        int nsum = sum;
        ULL nprod = prod;
        int ntight = tight;
        if (!started) {
            if (dig == 0) {
                ntight = (tight && (dig == limit));
                res += dp(pos + 1, ntight, 0, 0, 0, 1ULL, n, digits);
                continue;
            } else {
                nstarted = 1;
                nsum = dig;
                nprod = dig;
            }
        } else {
            nsum += dig;
            if (dig == 0) {
                nhasZero = 1;
                nprod = 0;
            } else {
                if (nprod != 0) nprod *= dig;
            }
        }
        if (tight && dig < limit) ntight = 0;
        res += dp(pos + 1, ntight, nstarted, nhasZero, nsum, nprod, n, digits);
    }
    memo[h].used = 1;
    memo[h].key = key;
    memo[h].value = res;
    return res;
}

long long countBeautiful(ULL X) {
    if (X == 0) return 0;
    int digits[20];
    int n = 0;
    while (X) {
        digits[n++] = X % 10;
        X /= 10;
    }
    for (int i = 0; i < n / 2; i++) {
        int tmp = digits[i];
        digits[i] = digits[n - 1 - i];
        digits[n - 1 - i] = tmp;
    }
    memset(memo, 0, sizeof(memo));
    return dp(0, 1, 0, 0, 0, 1ULL, n, digits);
}

int beautifulNumbers(int l, int r) {
    long long countR = countBeautiful(r);
    long long countL_1 = countBeautiful((ULL)l - 1);
    long long ans = countR - countL_1;
    return (int)ans;
}

