class Solution {
public:
    string digStr;
    int totLen;
    unordered_map<long long, long long> cacheA;
    unordered_map<long long, long long> cacheB;

    long long funB(int idx, int bd) {
        if(idx == totLen) return 1;
        long long keyVal = (long long)idx * 7 + bd;
        if(cacheB.find(keyVal) != cacheB.end())
            return cacheB[keyVal];

        int lim = bd ? (digStr[idx] - '0') : 9;
        long long tot = 0;
        for (int cur = 0; cur <= lim; cur++) {
            int nb = (bd && (cur == lim)) ? 1 : 0;
            tot += funB(idx + 1, nb);
        }
        cacheB[keyVal] = tot;
        return tot;
    }

    long long funA(int idx, int bd, int init, int zfl, int sm, int pr) {
        if(idx == totLen) {
            if(!init) return 0;
            if(zfl) return 1;
            return (pr % sm == 0) ? 1LL : 0LL;
        }

        long long key = ((((long long)idx * 3 + bd) * 3 + init) * 3 + zfl) * 100 + sm;
        key = key * 1000000000LL + pr;

        if(cacheA.find(key) != cacheA.end())
            return cacheA[key];

        int lim = bd ? (digStr[idx] - '0') : 9;
        long long ans = 0;
        for (int dig = 0; dig <= lim; dig++) {
            int nbd = (bd && (dig == lim)) ? 1 : 0;
            if(!init) {
                ans += (dig == 0) ? funA(idx + 1, nbd, 0, 0, 0, 1)
                                  : funA(idx + 1, nbd, 1, 0, dig, dig);
            } else {
                if(zfl)
                    ans += funB(idx + 1, nbd);
                else {
                    ans += (dig == 0) ? funB(idx + 1, nbd)
                                      : funA(idx + 1, nbd, 1, 0, sm + dig, pr * dig);
                }
            }
        }
        cacheA[key] = ans;
        return ans;
    }

    long long compVal(long long val) {
        if(val <= 0) return 0;
        digStr = to_string(val);
        totLen = (int)digStr.size();
        cacheA.clear();
        cacheB.clear();
        return funA(0, 1, 0, 0, 0, 1);
    }

    int beautifulNumbers(int l, int r) {
        long long posVal = compVal(r);
        long long negVal = compVal((long long)l - 1);
        return (int)(posVal - negVal);
    }
};

