typedef long long ll;

struct State {
    int pos;
    int tight;
    int started;
    int hasZero;
    int sum;
    ll prod;

    bool operator==(const State &other) const {
        return pos == other.pos && tight == other.tight && started == other.started &&
               hasZero == other.hasZero && sum == other.sum && prod == other.prod;
    }
};

struct StateHash {
    size_t operator()(const State &st) const {
        size_t res = 17;
        res = res * 31 + std::hash<int>()(st.pos);
        res = res * 31 + std::hash<int>()(st.tight);
        res = res * 31 + std::hash<int>()(st.started);
        res = res * 31 + std::hash<int>()(st.hasZero);
        res = res * 31 + std::hash<int>()(st.sum);
        res = res * 31 + std::hash<ll>()(st.prod);
        return res;
    }
};

class Solution {
public:
   string s;
    unordered_map<State, ll, StateHash> memo;

    ll solve(int pos, int tight, int started, int hasZero, int sum, ll prod) {
        if (pos == s.size()) {
            if (!started) return 0; 
            if (hasZero) return 1;
            return (prod % sum == 0) ? 1 : 0;
        }

        State st{pos, tight, started, hasZero, sum, prod};
        if (memo.count(st)) return memo[st];

        ll res = 0;
        int limit = (tight ? s[pos] - '0' : 9);
        for (int d = 0; d <= limit; d++) {
            int ntight = (tight && (d == limit));
            int nstarted = (started || (d != 0));
            int nhasZero = hasZero;
            int nsum = sum;
            ll nprod = prod;

            if (!started && d == 0) {
            } else if (d == 0) {
                nhasZero = 1;
                nsum += d;
                nprod = 0;
            } else {
                nsum += d;
                nprod = nprod * d;
            }
            res += solve(pos + 1, ntight, nstarted, nhasZero, nsum, nprod);
        }

        memo[st] = res;
        return res;
    }

    ll countBeautiful(ll n) {
        s = to_string(n);
        memo.clear();
        return solve(0, 1, 0, 0, 0, 1);
    }

    int beautifulNumbers(int l, int r) {
        return (int)(countBeautiful(r) - countBeautiful(l - 1));
    }
};

