#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define HSZ (1 << 15)

typedef long long ll;

typedef struct {
    int idx, tight, st, hz, sum, prod;
} State;

State htKey[HSZ];
ll htVal[HSZ];
int htUsed[HSZ];

unsigned int state_hash(State st) {
    unsigned int h = 0;
    h = st.idx;
    h = h * 37 + st.tight;
    h = h * 37 + st.st;
    h = h * 37 + st.hz;
    h = h * 37 + st.sum;
    h = h * 37 + st.prod;
    return h;
}

int state_equal(State a, State b) {
    return a.idx == b.idx && a.tight == b.tight && a.st == b.st &&
           a.hz == b.hz && a.sum == b.sum && a.prod == b.prod;
}

int digArr[20], dLen;

ll dp_calc(int idx, int tight, int st, int hz, int s, int p) {
    if (idx == dLen) {
        if (!st) return 0;
        if (hz) return 1;
        return (p % s == 0) ? 1 : 0;
    }

    State cur = {idx, tight, st, hz, s, p};

    unsigned int hkey = state_hash(cur);
    unsigned int pos = hkey & (HSZ - 1);
    while (htUsed[pos]) {
        if (state_equal(htKey[pos], cur))
            return htVal[pos];
        pos = (pos + 1) & (HSZ - 1);
    }

    ll res = 0;
    int up = (tight ? digArr[idx] : 9);
    for (int d = 0; d <= up; d++) {
        int nt = (tight && (d == up)) ? 1 : 0;
        if (!st) {
            if (d == 0) {
                res += dp_calc(idx + 1, nt, 0, 0, 0, 1);
            } else {
                res += dp_calc(idx + 1, nt, 1, 0, d, d);
            }
        } else {
            if (d == 0) {
                res += dp_calc(idx + 1, nt, 1, 1, s, 0);
            } else {
                if (hz) {
                    res += dp_calc(idx + 1, nt, 1, 1, s + d, 0);
                } else {
                    res += dp_calc(idx + 1, nt, 1, 0, s + d, p * d);
                }
            }
        }
    }

    pos = hkey & (HSZ - 1);
    while (htUsed[pos])
        pos = (pos + 1) & (HSZ - 1);
    htUsed[pos] = 1;
    htKey[pos] = cur;
    htVal[pos] = res;
    return res;
}

ll count_beauty(int n) {
    if (n <= 0) return 0;
    char buf[25];
    sprintf(buf, "%d", n);
    dLen = strlen(buf);
    for (int i = 0; i < dLen; i++) {
        digArr[i] = buf[i] - '0';
    }
    memset(htUsed, 0, sizeof(htUsed));
    return dp_calc(0, 1, 0, 0, 0, 1);
}

int beautifulNumbers(int L, int R) {
    ll totR = count_beauty(R);
    ll totL = count_beauty(L - 1);
    return (int)(totR - totL);
}

