#include <vector>
#include <tuple>
#include <string>
#include <unordered_map>
using namespace std;
class Solution {
public:



struct StateHash {
    size_t operator()(const tuple<int, int, int, int, int, int>& state) const {
        auto [pos, tight, started, hasZero, sum, prod] = state;
        size_t res = 17;
        res = res * 31 + std::hash<int>()(pos);
        res = res * 31 + std::hash<int>()(tight);
        res = res * 31 + std::hash<int>()(started);
        res = res * 31 + std::hash<int>()(hasZero);
        res = res * 31 + std::hash<int>()(sum);
        res = res * 31 + std::hash<int>()(prod);
        return res;
    }
};


unordered_map<tuple<int,int,int,int,int,int>, long long, StateHash> memo;


vector<int> digits;

long long dp(int pos, int tight, int started, int hasZero, int sum, int prod) {
    if(pos == digits.size()){
        if(!started) return 0;
        if(hasZero) return 1;
        return (prod % sum == 0) ? 1LL : 0LL;
    }

    auto state = make_tuple(pos, tight, started, hasZero, sum, prod);
    if(memo.find(state) != memo.end())
        return memo[state];

    long long ways = 0;
    int limit = (tight ? digits[pos] : 9);
    for (int d = 0; d <= limit; d++){
        int newTight = tight && (d == limit);
        int newStarted = started;
        int newHasZero = hasZero;
        int newSum = sum;
        int newProd = prod;

        if (!started) {
            if(d > 0){
                newStarted = 1;
                newSum = sum + d;
                newProd = d;  
            } 
        } else {
            newSum = sum + d;
            if(d == 0){
                newHasZero = 1;
                newProd = 0;
            } else {
                if(newHasZero)
                    newProd = 0;
                else
                    newProd = prod * d;
            }
        }

        ways += dp(pos + 1, newTight, newStarted, newHasZero, newSum, newProd);
    }

    memo[state] = ways;
    return ways;
}

long long countBeautiful(long long x) {
    if(x <= 0) return 0;
    digits.clear();
    while(x){
        digits.push_back(x % 10);
        x /= 10;
    }
    reverse(digits.begin(), digits.end());

    memo.clear();
    return dp(0, 1, 0, 0, 0, 1);
}

    int beautifulNumbers(int l, int r) {
        return countBeautiful(r) - countBeautiful(l - 1);
    }
};

