class Solution:
    def __init__(self):
        self.num_str = ""
        self.memo = {}

    def helper(self, idx, is_tight, has_started, has_zero, digit_sum, cnt2, cnt3, cnt5, cnt7):
        if idx == len(self.num_str):
            if not has_started:
                return 0
            if has_zero:
                return 1
            product = (2 ** cnt2) * (3 ** cnt3) * (5 ** cnt5) * (7 ** cnt7)
            return 1 if digit_sum != 0 and product % digit_sum == 0 else 0

        key = (idx, is_tight, has_started, has_zero, digit_sum, cnt2, cnt3, cnt5, cnt7)
        if key in self.memo:
            return self.memo[key]

        limit = int(self.num_str[idx]) if is_tight else 9
        result = 0

        for digit in range(limit + 1):
            new_tight = is_tight and (digit == limit)
            if not has_started:
                if digit == 0:
                    result += self.helper(idx + 1, new_tight, 0, 0, 0, 0, 0, 0, 0)
                else:
                    new_sum = digit
                    new_cnt2 = new_cnt3 = new_cnt5 = new_cnt7 = 0
                    temp = digit
                    while temp % 2 == 0:
                        new_cnt2 += 1
                        temp //= 2
                    while temp % 3 == 0:
                        new_cnt3 += 1
                        temp //= 3
                    while temp % 5 == 0:
                        new_cnt5 += 1
                        temp //= 5
                    while temp % 7 == 0:
                        new_cnt7 += 1
                        temp //= 7
                    result += self.helper(idx + 1, new_tight, 1, 0, new_sum, new_cnt2, new_cnt3, new_cnt5, new_cnt7)
            else:
                if has_zero:
                    result += self.helper(idx + 1, new_tight, 1, 1, digit_sum + digit, 0, 0, 0, 0)
                else:
                    if digit == 0:
                        result += self.helper(idx + 1, new_tight, 1, 1, digit_sum, 0, 0, 0, 0)
                    else:
                        new_sum = digit_sum + digit
                        new_cnt2, new_cnt3, new_cnt5, new_cnt7 = cnt2, cnt3, cnt5, cnt7
                        temp = digit
                        while temp % 2 == 0:
                            new_cnt2 += 1
                            temp //= 2
                        while temp % 3 == 0:
                            new_cnt3 += 1
                            temp //= 3
                        while temp % 5 == 0:
                            new_cnt5 += 1
                            temp //= 5
                        while temp % 7 == 0:
                            new_cnt7 += 1
                            temp //= 7
                        result += self.helper(idx + 1, new_tight, 1, 0, new_sum, new_cnt2, new_cnt3, new_cnt5, new_cnt7)

        self.memo[key] = result
        return result

    def count_beautiful(self, num):
        if num < 1:
            return 0
        self.num_str = str(num)
        self.memo.clear()
        return self.helper(0, 1, 0, 0, 0, 0, 0, 0, 0)

    def beautifulNumbers(self, left: int, right: int) -> int:
        return self.count_beautiful(right) - self.count_beautiful(left - 1)

