class Solution {
public:
    using ll=long long;
    string str;
    unordered_map<ll,ll>dp;
    ll recur(int pos,int l,int r,int q,int sum,int prod)
    {
        return ((ll)pos | ((ll)l<<6) | ((ll)r<<7) | ((ll)q<<8) | ((ll)sum<<9) | ((ll)prod<<16));
    }
    ll memo(int pos,int l,int r,int q,int sum,int prod)
    {
        if(pos==str.size())
        {
            if(!r)return 0;
            if(q)return 1;
            return (sum!=0 && (prod%sum==0))?1LL:0LL;
        }
        ll key=recur(pos,l,r,q,sum,prod);
        if(dp.find(key)!=dp.end())return dp[key];
        ll ans=0;
        int lim=l?(str[pos]-'0'):9;
        if(!r)
        {
            for(int i=0;i<=lim;++i)
            {
                ans+=(i==0)?memo(pos+1,(l && i==lim),0,0,0,1):memo(pos+1,(l && i==lim),1,0,i,i);
            }
        }
        else
        {
            for(int i=0;i<=lim;++i)
            {
                int L=(l && i==lim),R=sum+i,t1=q,t2=prod;
                if(i==0)t1=1,t2=0;
                else if(t1==0)t2=prod*i;
                else t2=0;
                ans+=memo(pos+1,L,1,t1,R,t2);
            }
        }
        return dp[key]=ans;
    }
    int beautifulNumbers(int l,int r) {
        if(l<1) return 0;
        str=to_string(r),dp.clear();
        ll L=memo(0,1,0,0,0,1);
        str=to_string(l-1),dp.clear();
        ll R=memo(0,1,0,0,0,1);
        return (int)(L-R);
    }
};

