#define all(x) (x).begin(), (x).end()
#define sz(x) (int)(x).size()
#define F(i, a, b) for (int i = a; i < b; i++)
#define ll long long

class Solution {
public:
    string s;
    map<tuple<int, int, int, int, int, int>, int> dp;

    int f(int p, int t, int st, int z, int sm, int pr) {
        if (p == sz(s)) return st && (z || (sm && pr % sm == 0));
        auto k = make_tuple(p, t, st, z, sm, pr);
        if (dp.count(k)) return dp[k];
        int r = 0, lim = t ? s[p] - '0' : 9;
        F(d, 0, lim + 1) {
            int nt = t && (d == lim);
            if (!st) r += f(p + 1, nt, d > 0, 0, d, max(d, 1));
            else r += f(p + 1, nt, 1, z || d == 0, sm + d, z ? 0 : pr * max(d, 1));
        }
        return dp[k] = r;
    }

    int cntB(int n) {
        s = to_string(n);
        dp.clear();
        return f(0, 1, 0, 0, 0, 1);
    }

    int beautifulNumbers(int l, int r) {
        return cntB(r) - cntB(l - 1);
    }
};

