class Solution {
public:
    int intpow(int a, int b) {
        if (b==0)return 1;
        else return a * intpow(a, b-1);
    }

    map<vector<int>, int> MEMO;
    vector<int> PRE = {0,9,15,309,3675,43462,486887,5311174,57110375,607393752};

    int complete(int D, int P, int S) {
        if (D == 0) {
            if (S > 0 && P % S == 0) {
                return 1;
            }
            else return 0;
        }

        vector<int> key = {D,P,S};
        if (MEMO.count(key)) return MEMO[key];

        int ans = 0;
        for (int d = 0; d < 10; d++) {
            ans += complete(D - 1, P * d, S + d);
        }
        MEMO[key] = ans;
        return MEMO[key];
    }

    // Compute the number of beautifulNumbers less than X
    int solve(int X) {
        if (X < 10) return X;
        string s = to_string(X);
        int P = 1;
        int S = 0;
        int ans = 0;
        for (int d = 0; d < s.size(); d++) {
            int val = 0;
            if (d == 0) val = 1;
            int cur = (int)(s[d] - '0');
            for (int v = val; v < cur; v++) {
                //D is number of remaining digits
                int D = s.size() - d - 1;
                ans += complete(D, P * v, S + v);
            }

            P *= cur;
            S += cur;
        }
        if (S > 0 && P % S == 0) ans++;

        for (int i = 1; i < s.size(); i++) {
            ans += PRE[i];
        }
        return ans;
    }

    int beautifulNumbers(int l, int r) {
        //Count the number of integers between l and r
        // 1,2,3,4,5,6,7,8,9

        int ans = solve(r) - solve(l-1);
        return ans;
    }
};

