class Solution {
public:
    // long long dp[10][100][100][2][2];
    unordered_map<string, int>dp;
    long long solve(string str, int i, long long p, int s, int tight, int lead){
           if(i == (int)str.size()){
                if(lead) return 0;
                if(s == 0) return 0;
                if(p%s == 0) return 1;
                 return 0;
           }
         int ub = tight ? (str[i]-'0') : 9;
        string ok = to_string(i) + "*" + to_string(p) + "*" + to_string(s) + "*" + to_string(tight) + "*" + to_string(lead);
         if(dp.count(ok)) return dp[ok];
        long long ans = 0;
         for(int d = 0; d <= ub; d++){
               if(lead && d == 0){
                    ans += solve(str, i+1, (p), s, tight&(d == ub), 1);
               }
               else
               ans += solve(str, i+1, (p*1LL*d), s+d, tight&(d == ub), lead & (d == 0));
         }
         return dp[ok] = ans;
    }
    int beautifulNumbers(int l, int r) {
      // memset(dp, -1, sizeof(dp));
      long long R = solve(to_string(r), 0, 1, 0, 1, 1);
         // memset(dp, -1, sizeof(dp));
        dp.clear();
      long long L = solve(to_string(l-1), 0, 1, 0, 1, 1);
        return R - L;
    }
};

