class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        s = ""
        dp = {}

        def rec(pos, tight, started, has_zero, sum_digits, e2, e3, e5, e7):
            if pos == len(s):
                if not started:
                    return 0
                if has_zero:
                    return 1

                prod = 1
                for _ in range(e2):
                    prod *= 2
                for _ in range(e3):
                    prod *= 3
                for _ in range(e5):
                    prod *= 5
                for _ in range(e7):
                    prod *= 7

                return 1 if (sum_digits != 0 and prod % sum_digits == 0) else 0

            key = (pos, tight, started, has_zero, sum_digits, e2, e3, e5, e7)
            if key in dp:
                return dp[key]

            res = 0
            limit = int(s[pos]) if tight else 9

            for d in range(limit + 1):
                ntight = tight and (d == limit)

                if not started:
                    if d == 0:
                        res += rec(pos + 1, ntight, 0, 0, 0, 0, 0, 0, 0)
                    else:
                        new_sum = d
                        new_e2, new_e3, new_e5, new_e7 = 0, 0, 0, 0
                        temp = d

                        while temp % 2 == 0:
                            new_e2 += 1
                            temp //= 2
                        while temp % 3 == 0:
                            new_e3 += 1
                            temp //= 3
                        while temp % 5 == 0:
                            new_e5 += 1
                            temp //= 5
                        while temp % 7 == 0:
                            new_e7 += 1
                            temp //= 7

                        res += rec(pos + 1, ntight, 1, 0, new_sum, new_e2, new_e3, new_e5, new_e7)
                else:
                    if has_zero:
                        new_sum = sum_digits + d
                        res += rec(pos + 1, ntight, 1, 1, new_sum, 0, 0, 0, 0)
                    else:
                        if d == 0:
                            new_sum = sum_digits
                            res += rec(pos + 1, ntight, 1, 1, new_sum, 0, 0, 0, 0)
                        else:
                            new_sum = sum_digits + d
                            new_e2, new_e3, new_e5, new_e7 = e2, e3, e5, e7
                            temp = d

                            while temp % 2 == 0:
                                new_e2 += 1
                                temp //= 2
                            while temp % 3 == 0:
                                new_e3 += 1
                                temp //= 3
                            while temp % 5 == 0:
                                new_e5 += 1
                                temp //= 5
                            while temp % 7 == 0:
                                new_e7 += 1
                                temp //= 7

                            res += rec(pos + 1, ntight, 1, 0, new_sum, new_e2, new_e3, new_e5, new_e7)

            dp[key] = res
            return res

        def countBeautiful(x):
            if x < 1:
                return 0

            nonlocal s, dp
            s = str(x)
            dp = {}
            return rec(0, 1, 0, 0, 0, 0, 0, 0, 0)

        return countBeautiful(r) - countBeautiful(l - 1)

