class Solution:
    def __init__(self):
        self.s = ""
        self.dp = {}

    def rec(self, pos, tight, started, has_zero, s, e2, e3, e5, e7):
        if pos == len(self.s):
            if not started:
                return 0
            if has_zero:
                return 1
            prod = (2 ** e2) * (3 ** e3) * (5 ** e5) * (7 ** e7)
            return 1 if s and prod % s == 0 else 0

        key = (pos, tight, started, has_zero, s, e2, e3, e5, e7)
        if key in self.dp:
            return self.dp[key]

        res = 0
        limit = int(self.s[pos]) if tight else 9
        for d in range(limit + 1):
            ntight = tight and (d == limit)
            if not started:
                if d == 0:
                    res += self.rec(pos + 1, ntight, 0, 0, 0, 0, 0, 0, 0)
                else:
                    temp, ne2, ne3, ne5, ne7 = d, 0, 0, 0, 0
                    while temp % 2 == 0: ne2 += 1; temp //= 2
                    while temp % 3 == 0: ne3 += 1; temp //= 3
                    while temp % 5 == 0: ne5 += 1; temp //= 5
                    while temp % 7 == 0: ne7 += 1; temp //= 7
                    res += self.rec(pos + 1, ntight, 1, 0, d, ne2, ne3, ne5, ne7)
            else:
                new_sum = s + d
                if has_zero or d == 0:
                    res += self.rec(pos + 1, ntight, 1, 1, new_sum, 0, 0, 0, 0)
                else:
                    temp, ne2, ne3, ne5, ne7 = d, e2, e3, e5, e7
                    while temp % 2 == 0: ne2 += 1; temp //= 2
                    while temp % 3 == 0: ne3 += 1; temp //= 3
                    while temp % 5 == 0: ne5 += 1; temp //= 5
                    while temp % 7 == 0: ne7 += 1; temp //= 7
                    res += self.rec(pos + 1, ntight, 1, 0, new_sum, ne2, ne3, ne5, ne7)

        self.dp[key] = res
        return res

    def countBeautiful(self, x):
        if x < 1:
            return 0
        self.s = str(x)
        self.dp.clear()
        return self.rec(0, 1, 0, 0, 0, 0, 0, 0, 0)

    def beautifulNumbers(self, l, r):
        return self.countBeautiful(r) - self.countBeautiful(l - 1)

