class Solution {
    String s;
    Map<String, Long> map;

    long countBeautiful(int X) {
        s = Integer.toString(X);
        map = new HashMap<>();
        return func(0, 1, 0, 0, 0, 1);
    }

    int beautifulNumbers(int l, int r) {
        long ans =  (countBeautiful(r) - (l > 1 ? countBeautiful(l - 1) : 0));
        return (int)ans;
    }

    long func(int ind, int tight, int st, int zero, int sum, int pro) {
        if (ind == s.length()) {
            if (st == 0) return 0;
            if (zero == 1) return 1;
            return (pro % sum == 0) ? 1 : 0;
        }
        String key = ind + "/" + tight + "/" + st + "/" + zero + "*" + sum + "+" + pro;
        if (map.containsKey(key)) return map.get(key);
        long res = 0;
        int lim = tight == 1 ? s.charAt(ind) - '0' : 9;
        for (int d = 0; d <= lim; d++) {
            int ntight = (tight == 1 && (d == lim)) ? 1 : 0;
            if (st == 0) {
                if (d == 0) res += func(ind + 1, ntight, 0, 0, 0, 1);
                else res += func(ind + 1, ntight, 1, 0, d, d);
            } else {
                int isZero = zero;
                if (d == 0) isZero = 1;
                res += func(ind + 1, ntight, 1, isZero, sum + d, (d == 0 ? 0 : pro * d));
            }
        }
        map.put(key, res);
        return res;
    }


}

