#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    typedef long long ll;
    string s;
    unordered_map<ll, ll> dp;

    ll generateKey(int pos, int tight, int started, int hasZero, int sum, int e2, int e3, int e5, int e7) {
        ll key = pos;
        key |= ((ll)tight << 4);
        key |= ((ll)started << 5);
        key |= ((ll)hasZero << 6);
        key |= ((ll)sum << 7);
        key |= ((ll)e2 << (7 + 7));
        key |= ((ll)e3 << (7 + 7 + 6));
        key |= ((ll)e5 << (7 + 7 + 6 + 6));
        key |= ((ll)e7 << (7 + 7 + 6 + 6 + 5));
        return key;
    }

    bool isValid(int sum, int e2, int e3, int e5, int e7) {
        if (sum == 0) return false;
        int prod = 1;
        for (int i = 0; i < e2; i++) prod *= 2;
        for (int i = 0; i < e3; i++) prod *= 3;
        for (int i = 0; i < e5; i++) prod *= 5;
        for (int i = 0; i < e7; i++) prod *= 7;
        return (prod % sum == 0);
    }

    tuple<int, int, int, int> calculatePrimeFactors(int num) {
        int e2 = 0, e3 = 0, e5 = 0, e7 = 0;
        while (num % 2 == 0) { e2++; num /= 2; }
        while (num % 3 == 0) { e3++; num /= 3; }
        while (num % 5 == 0) { e5++; num /= 5; }
        while (num % 7 == 0) { e7++; num /= 7; }
        return {e2, e3, e5, e7};
    }

    ll rec(int pos, int tight, int started, int hasZero, int sum, int e2, int e3, int e5, int e7) {
        if (pos == s.size()) {
            return started && (hasZero || isValid(sum, e2, e3, e5, e7)) ? 1 : 0;
        }

        ll key = generateKey(pos, tight, started, hasZero, sum, e2, e3, e5, e7);
        if (dp.find(key) != dp.end()) return dp[key];

        ll res = 0;
        int limit = tight ? s[pos] - '0' : 9;
        for (int d = 0; d <= limit; d++) {
            int ntight = tight && (d == limit);
            res += processDigit(pos, ntight, started, hasZero, sum, e2, e3, e5, e7, d);
        }
        return dp[key] = res;
    }

    ll processDigit(int pos, int ntight, int started, int hasZero, int sum, int e2, int e3, int e5, int e7, int d) {
        if (!started) {
            return (d == 0) ? rec(pos + 1, ntight, 0, 0, 0, 0, 0, 0, 0)
                            : rec(pos + 1, ntight, 1, 0, d, get<0>(calculatePrimeFactors(d)), get<1>(calculatePrimeFactors(d)), get<2>(calculatePrimeFactors(d)), get<3>(calculatePrimeFactors(d)));
        }
        if (hasZero) {
            return rec(pos + 1, ntight, 1, 1, sum + d, 0, 0, 0, 0);
        }
        if (d == 0) {
            return rec(pos + 1, ntight, 1, 1, sum, 0, 0, 0, 0);
        }
        auto [new_e2, new_e3, new_e5, new_e7] = calculatePrimeFactors(d);
        return rec(pos + 1, ntight, 1, 0, sum + d, e2 + new_e2, e3 + new_e3, e5 + new_e5, e7 + new_e7);
    }

    ll countBeautiful(ll x) {
        if (x < 1) return 0;
        s = to_string(x);
        dp.clear();
        return rec(0, 1, 0, 0, 0, 0, 0, 0, 0);
    }

    int beautifulNumbers(int l, int r) {
        return static_cast<int>(countBeautiful(r) - countBeautiful(l - 1));
    }
};

