typedef long long ll;

class Solution {
private:
    string digits;
    unordered_map<string, ll> memo;

public:

    ll dp(int pos, int tight, int started, int hasZero, int sum, int prod)
    {
        if (pos == digits.size())
        {
            if (!started)
                return 0;
            if (hasZero)
                return 1;
            return (sum != 0 && prod % sum == 0) ? 1 : 0;
        }

        string key = to_string(pos) + "_" + to_string(tight) + "_" +
                     to_string(started) + "_" + to_string(hasZero) + "_" +
                     to_string(sum) + "_" + to_string(prod);

        if (memo.find(key) != memo.end())
            return memo[key];

        ll res = 0;
        int limit = tight ? digits[pos] - '0' : 9;

        for (int d = 0; d <= limit; d++)
        {
            int ntight = tight && (d == limit);
            int nstarted = started;
            int nhasZero = hasZero;
            int nsum = sum;
            int nprod = prod;

            if (!started)
            {
                if (d == 0)
                {
                    res += dp(pos + 1, ntight, 0, 0, 0, 1);
                    continue;
                }
                else
                {
                    nstarted = 1;
                    nsum = d;
                    nprod = d;
                }
            }
            else
            {
                nsum += d;
                if (d == 0)
                {
                    nhasZero = 1;
                    nprod = 0;
                }
                else
                {
                    if (nhasZero)
                        nprod = 0;
                    else
                        nprod *= d;
                }
            }

            res += dp(pos + 1, ntight, nstarted, nhasZero, nsum, nprod);
        }

        memo[key] = res;
        return res;
    }

    int solve(int x) {
        if (x < 1) {
            return 0;
        }
        digits = to_string(x);
        memo.clear();
        return dp(0, 1, 0, 0, 0, 1);
    }

    int beautifulNumbers(int l, int r) { return solve(r) - solve(l - 1); }
};

