class Solution {
    private String numberString;
    private Map<Long, Long> dp;

    public int beautifulNumbers(int l, int r) {
        return (int) (countBeautifulNumbers(r) - countBeautifulNumbers(l - 1));
    }

    private long countBeautifulNumbers(long x) {
        if (x < 1) return 0;
        numberString = Long.toString(x);
        dp = new HashMap<>();
        return recursiveCount(0, 1, 0, 0, 0, 0, 0, 0, 0);
    }

    private long recursiveCount(int pos, int tight, int started, int hasZero, int sum, int e2, int e3, int e5, int e7) {
        if (pos == numberString.length()) {
            if (started == 0) return 0;
            if (hasZero == 1) return 1;
            return (sum != 0 && calculateProduct(e2, e3, e5, e7) % sum == 0) ? 1 : 0;
        }

        long key = generateKey(pos, tight, started, hasZero, sum, e2, e3, e5, e7);
        if (dp.containsKey(key)) return dp.get(key);

        long result = 0;
        int limit = (tight == 1) ? numberString.charAt(pos) - '0' : 9;

        for (int digit = 0; digit <= limit; digit++) {
            int newTight = (tight == 1 && digit == limit) ? 1 : 0;
            result += processDigit(pos, newTight, started, hasZero, sum, e2, e3, e5, e7, digit);
        }

        dp.put(key, result);
        return result;
    }

    private long processDigit(int pos, int newTight, int started, int hasZero, int sum, int e2, int e3, int e5, int e7, int digit) {
        if (started == 0) {
            if (digit == 0) {
                return recursiveCount(pos + 1, newTight, 0, 0, 0, 0, 0, 0, 0);
            } else {
                int[] factors = getFactors(digit, 0, 0, 0, 0);
                return recursiveCount(pos + 1, newTight, 1, 0, digit, factors[0], factors[1], factors[2], factors[3]);
            }
        } 

        if (hasZero == 1) {
            return recursiveCount(pos + 1, newTight, 1, 1, sum + digit, 0, 0, 0, 0);
        } 

        if (digit == 0) {
            return recursiveCount(pos + 1, newTight, 1, 1, sum, 0, 0, 0, 0);
        } 

        int[] factors = getFactors(digit, e2, e3, e5, e7);
        return recursiveCount(pos + 1, newTight, 1, 0, sum + digit, factors[0], factors[1], factors[2], factors[3]);
    }

    private int calculateProduct(int e2, int e3, int e5, int e7) {
        return (int) (Math.pow(2, e2) * Math.pow(3, e3) * Math.pow(5, e5) * Math.pow(7, e7));
    }

    private long generateKey(int pos, int tight, int started, int hasZero, int sum, int e2, int e3, int e5, int e7) {
        return ((long) pos) | ((long) tight << 4) | ((long) started << 5) | ((long) hasZero << 6) | ((long) sum << 7)
                | ((long) e2 << (7 + 7)) | ((long) e3 << (7 + 7 + 6)) | ((long) e5 << (7 + 7 + 6 + 6)) | ((long) e7 << (7 + 7 + 6 + 6 + 5));
    }

    private int[] getFactors(int num, int e2, int e3, int e5, int e7) {
        while (num % 2 == 0) { e2++; num /= 2; }
        while (num % 3 == 0) { e3++; num /= 3; }
        while (num % 5 == 0) { e5++; num /= 5; }
        while (num % 7 == 0) { e7++; num /= 7; }
        return new int[]{e2, e3, e5, e7};
    }
}

