typedef long long ll;

struct State {
    int pos, tight, started, hasZero, sum;
    ll prod;
    bool operator==(const State &other) const {
        return pos==other.pos && tight==other.tight &&
               started==other.started && hasZero==other.hasZero &&
               sum==other.sum && prod==other.prod;
    }
};

struct StateHash {
    std::size_t operator()(const State &s) const {
        size_t res = 17;
        res = res * 31 + hash<int>()(s.pos);
        res = res * 31 + hash<int>()(s.tight);
        res = res * 31 + hash<int>()(s.started);
        res = res * 31 + hash<int>()(s.hasZero);
        res = res * 31 + hash<int>()(s.sum);
        res = res * 31 + hash<ll>()(s.prod);
        return res;
    }
};

string digits;
unordered_map<State, ll, StateHash> memo;

ll dp(int pos, int tight, int started, int hasZero, int sum, ll prod) {
    if (pos == (int)digits.size()) {
        if (!started) return 0;
        if (hasZero) return 1;
        return (prod % sum == 0) ? 1 : 0;
    }

    State state{pos, tight, started, hasZero, sum, prod};
    if(memo.count(state)) return memo[state];

    ll res = 0;
    int limit = tight ? (digits[pos]-'0') : 9;
    for (int d = 0; d <= limit; d++) {
        int ntight = (tight && (d == limit)) ? 1 : 0;
        if (!started) {
            if (d == 0) {
                res += dp(pos+1, ntight, 0, 0, 0, 1);
            } else {
                res += dp(pos+1, ntight, 1, 0, d, d);
            }
        } else {
            if (hasZero) {
                res += dp(pos+1, ntight, 1, 1, sum, prod);
            } else {
                if (d == 0) {
                    res += dp(pos+1, ntight, 1, 1, sum, prod);
                } else {
                    res += dp(pos+1, ntight, 1, 0, sum + d, prod * d);
                }
            }
        }
    }

    memo[state] = res;
    return res;
}
ll f(ll x) {
    digits = to_string(x);
    memo.clear();
    return dp(0, 1, 0, 0, 0, 1);
}

class Solution {
public:
    int beautifulNumbers(int l, int r) {
        return f(r) - f(l - 1);
    }
};

