class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def final(n):
            memo = {}
            n = str(n)
            def fun(i,tight,sum,product,zero,leading_zero):
                if i==len(n):
                    if (sum>0 and product%sum==0) or (zero and not leading_zero):
                        return 1
                    return 0
                if (i,tight,sum,product,zero, leading_zero) in memo:
                    return memo[(i,tight,sum,product,zero,leading_zero)]
                if tight:
                    end = int(n[i])
                else:
                    end = 9
                res = 0
                for j in range(end+1):
                    res+=fun(i+1,int(tight&(j==int(n[i]))),sum+j, (product*j if j>0 else product),int((zero or (j==0)) and not leading_zero),int(leading_zero&(j==0)))
                memo[(i,tight,sum,product,zero,leading_zero)] = res
                return res
            return fun(0,1,0,1,0,1)
        return final(r) - final(l-1)

