using namespace IO;

class Solution {
public:
    struct E {
        int pos, pref, st, sm; ll p;

        bool operator < (const E &other) const {
            if (pos != other.pos) return pos < other.pos;
            if (pref != other.pref) return pref < other.pref;
            if (st != other.st) return st < other.st;
            if (sm != other.sm) return sm < other.sm;
            return p < other.p;
        }
    };

    map<E, ll> dp; string s;

    ll dfs(string s, int pos, int pref, int st, int sm, ll p) {
        if (pos == sz(s)) {
            if (!st) {
                return 0;
            }
            if (!p) {
                return 1;
            }

            return (sm != 0 && !(p % sm));
        }

        E cur = { pos, pref, st, sm, p };
        if (dp.find(cur) != dp.end()) {
            return dp[cur];
        }

        ll cnt = 0; int b = pref ? (s[pos] - '0') : 9;
        for (int i = 0; i <= b; i++) {
            int nxt1 = (pref && i == b), nxt2 = st, nxt3 = sm; ll nxt4 = p;

            if (!st) {
                if (!i) {
                    cnt += dfs(s, pos + 1, nxt1, 0, 0, 1);
                } else {
                    cnt += dfs(s, pos + 1, nxt1, 1, i, i);
                }
            } else {
                nxt3 = i + sm;
                if (!i) {
                    nxt4 = 0LL;
                } else {
                    nxt4 = (!p ? p : i * p);
                }
                cnt += dfs(s, pos + 1, nxt1, nxt2, nxt3, nxt4);
            }
        }
        return dp[cur] = cnt;
    }

    int beautifulNumbers(int l, int r) {
        int R = dfs(to_string(r), 0, 1, 0, 0, 1);
        dp.clear();
        int L;
        if (l == 1) L = 0;
        else L = dfs(to_string(l - 1), 0, 1, 0, 0, 1);
        return R - L;
    }
};

