class Solution {
    private String s;
    private Map<Long, Long> dp;

    private long rec(int pos, int tight, int started, int hasZero, int sum, int e2, int e3, int e5, int e7) {
        if (pos == s.length()) {
            if (started == 0) return 0;
            if (hasZero == 1) return 1;
            int prod = (int) (Math.pow(2, e2) * Math.pow(3, e3) * Math.pow(5, e5) * Math.pow(7, e7));
            return (sum != 0 && prod % sum == 0) ? 1 : 0;
        }

        long key = (long) pos | ((long) tight << 4) | ((long) started << 5) | ((long) hasZero << 6) | ((long) sum << 7) |
                   ((long) e2 << 14) | ((long) e3 << 20) | ((long) e5 << 26) | ((long) e7 << 31);

        if (dp.containsKey(key)) return dp.get(key);

        long res = 0;
        int limit = tight == 1 ? s.charAt(pos) - '0' : 9;

        for (int d = 0; d <= limit; d++) {
            int ntight = (tight == 1 && d == limit) ? 1 : 0;
            if (started == 0) {
                if (d == 0) {
                    res += rec(pos + 1, ntight, 0, 0, 0, 0, 0, 0, 0);
                } else {
                    int newSum = d, new_e2 = 0, new_e3 = 0, new_e5 = 0, new_e7 = 0, temp = d;
                    while (temp % 2 == 0) { new_e2++; temp /= 2; }
                    while (temp % 3 == 0) { new_e3++; temp /= 3; }
                    while (temp % 5 == 0) { new_e5++; temp /= 5; }
                    while (temp % 7 == 0) { new_e7++; temp /= 7; }
                    res += rec(pos + 1, ntight, 1, 0, newSum, new_e2, new_e3, new_e5, new_e7);
                }
            } else {
                if (hasZero == 1) {
                    res += rec(pos + 1, ntight, 1, 1, sum + d, 0, 0, 0, 0);
                } else {
                    if (d == 0) {
                        res += rec(pos + 1, ntight, 1, 1, sum, 0, 0, 0, 0);
                    } else {
                        int newSum = sum + d, new_e2 = e2, new_e3 = e3, new_e5 = e5, new_e7 = e7, temp = d;
                        while (temp % 2 == 0) { new_e2++; temp /= 2; }
                        while (temp % 3 == 0) { new_e3++; temp /= 3; }
                        while (temp % 5 == 0) { new_e5++; temp /= 5; }
                        while (temp % 7 == 0) { new_e7++; temp /= 7; }
                        res += rec(pos + 1, ntight, 1, 0, newSum, new_e2, new_e3, new_e5, new_e7);
                    }
                }
            }
        }
        dp.put(key, res);
        return res;
    }

    private long countBeautiful(long x) {
        if (x < 1) return 0;
        s = Long.toString(x);
        dp = new HashMap<>();
        return rec(0, 1, 0, 0, 0, 0, 0, 0, 0);
    }
    public int beautifulNumbers(int l, int r) {
        return (int) (countBeautiful(r) - countBeautiful(l-1));
    }
}

