class Solution {

        private Map<String, Long> memo;

    public int beautifulNumbers(int l, int r) {
        long countR = countBeautiful(r);
        long countL = countBeautiful(l - 1);
        // The answer is the count in [l, r]
        return (int)(countR - countL);
    }

    // Returns the count of beautiful numbers in [1, X].
    private long countBeautiful(int X) {
        // If X is less than 1, there are no positive numbers.
        if (X < 1) return 0;
        // Convert X to its digit array (most-significant digit first)
        String s = Integer.toString(X);
        int[] digits = new int[s.length()];
        for (int i = 0; i < s.length(); i++) {
            digits[i] = s.charAt(i) - '0';
        }
        memo = new HashMap<>();
        // Start with pos=0, tight=1, not started, no zero encountered,
        // current sum=0, and product=1 (multiplicative identity)
        return dp(0, 1, 0, 0, 0, 1, digits);
    }

    /**
     * DP function that returns the count of beautiful numbers for the state.
     * 
     * @param pos    Current digit position (0-indexed)
     * @param tight  1 if the current prefix is equal to the prefix of the target, else 0.
     * @param started 1 if we have already chosen a non-leading digit; 0 otherwise.
     * @param hasZero 1 if a zero has been chosen after starting; 0 otherwise.
     * @param sum    Running sum of digits (only valid if hasZero==0 and started==1)
     * @param prod   Running product of digits (only valid if hasZero==0 and started==1)
     * @param digits The array of digits for the target number.
     * @return       The count of beautiful numbers for the current state.
     */
    private long dp(int pos, int tight, int started, int hasZero, int sum, int prod, int[] digits) {
        // Base case: all positions have been processed.
        if (pos == digits.length) {
            // If no number has been formed, we return 0.
            if (started == 0) return 0;
            // If we already encountered a zero, the number is automatically beautiful.
            if (hasZero == 1) return 1;
            // Otherwise, check if the product is divisible by the sum.
            return (prod % sum == 0) ? 1 : 0;
        }

        // Build a key for memoization.
        // For states where hasZero==1, sum and prod are not relevant, so we use dummy values.
        String key = pos + "," + tight + "," + started + "," + hasZero + ",";
        if (hasZero == 1) {
            key += "0,0";
        } else {
            key += sum + "," + prod;
        }

        if (memo.containsKey(key)) {
            return memo.get(key);
        }

        long res = 0;
        int maxDigit = (tight == 1) ? digits[pos] : 9;

        // Try every possible digit at current position.
        for (int d = 0; d <= maxDigit; d++) {
            int newTight = (tight == 1 && d == maxDigit) ? 1 : 0;
            int newStarted = started;
            int newHasZero = hasZero;
            int newSum = sum;
            int newProd = prod;

            if (started == 0) {
                // We haven't started the number yet.
                if (d == 0) {
                    // Still not started; leading zero does not affect sum or product.
                    res += dp(pos + 1, newTight, newStarted, newHasZero, newSum, newProd, digits);
                    continue;
                } else {
                    // Start the number with a nonzero digit.
                    newStarted = 1;
                    newSum = d;
                    newProd = d;
                    // If the digit is zero (which won't happen here since d>0), we would set newHasZero = 1.
                }
            } else {
                // Already started.
                if (hasZero == 0) {
                    // Haven't encountered a zero yet.
                    if (d == 0) {
                        // Now we encounter a zero -> mark newHasZero.
                        newHasZero = 1;
                        // Once a zero is encountered, we no longer need sum and prod.
                        newSum = 0; // dummy
                        newProd = 0; // dummy
                    } else {
                        newSum = sum + d;
                        newProd = prod * d;
                    }
                }
                // If hasZero is already 1, no updates for sum and product are needed.
            }

            res += dp(pos + 1, newTight, newStarted, newHasZero, newSum, newProd, digits);
        }

        memo.put(key, res);
        return res;
    }
}

