struct TupleHash {
    size_t operator()(const tuple<int, int, int, int, int, int>& key) const {
        size_t seed = 0;
        auto hash_combine = [&seed](int v) { 
            seed ^= std::hash<int>{}(v) + 0x9e3779b97f4a7c15ULL + (seed << 6) + (seed >> 2);
        };
        hash_combine(static_cast<int>(get<0>(key)));
        hash_combine(static_cast<int>(get<1>(key)));
        hash_combine(static_cast<int>(get<2>(key)));
        hash_combine(static_cast<int>(get<3>(key)));
        hash_combine(static_cast<int>(get<4>(key)));
        hash_combine(static_cast<int>(get<5>(key)));
        return seed;
    }
};

class Solution {
public:
    using Key = tuple<int, int, int, int, int, int>;
    unordered_map<Key, long long, TupleHash> memo; 

    long long dp(int p, int t, int st, int zero, int sum, int prod, const vector<int>&a) {
        if(p==a.size())
        {
            if(!st)
                return 0;
            if(zero)
                return 1;
            return (prod%sum==0)?1LL:0LL;
        }
        Key state = make_tuple(p, t, st, zero, sum, prod);

        if(memo.count(state))
        {
            return memo[state];
        }
        long long int ans=0;
        int limit=t?a[p]:9;

        int d;

        for(d=0;d<=limit;d++)
            {
                int t1=(t&&d==limit)?1:0;

                int st1=st;
                int zero1=zero;
                int sum1=sum,prod1=prod;

                if(!st)
                {
                    if(d==0)
                    {
                        ans+=dp(p+1,t1,0,0,0,1,a);
                        continue;
                    }
                    else
                    {
                        st1=1;
                        sum1=d;
                        prod1=d;
                        zero1=(d==0)?1:0;
                    }
                }
                else
                {
                    if(!zero1)
                    {
                        if(d==0)
                        {
                            zero1=1;
                            sum1=prod1=0;
                        }
                        else
                        {
                            sum1+=d;
                            prod1*=d;
                        }
                    }
                }
                ans+=dp(p+1,t1,st1,zero1,sum1,prod1,a);
            }
        return memo[state]=ans;
    }

    long long f(long long x) {
        vector<int>v;
        while(x>0)
            {
                v.push_back(x%10);
                x=x/10;
            }
        if(v.size()==0)
            v.push_back(0);
        reverse(v.begin(),v.end());
        memo.clear();
        return dp(0, 1, 0, 0, 0, 1, v);
    }

    int beautifulNumbers(int l, int r) {
        return (int)(f(r) - f(l - 1));
    }
};

