#include <bits/stdc++.h>
using namespace std;
typedef long long ll;

class Solution {
public:
    string numStr;
    unordered_map<string, ll> dp;

    ll dfs(int idx, int hasStarted, int isTight, int digitSum, ll digitProd, int containsZero) {
        if (idx == numStr.size()) {
            if (!hasStarted) return 0;
            if (containsZero) return 1;
            return (digitProd % digitSum == 0) ? 1 : 0;
        }

        string state = to_string(idx) + "_" + to_string(hasStarted) + "_" + to_string(isTight) + "_" +
                       to_string(digitSum) + "_" + to_string(digitProd) + "_" + to_string(containsZero);

        if (dp.count(state)) return dp[state];


        int limit = (!isTight) ?9:  numStr[idx] - '0';
        ll count = 0;

        for (int digit = 0; digit <= limit; digit++) {
            int newTight = ((digit == limit) && isTight);

            if (!hasStarted) {
                if (digit == 0) count += dfs(idx + 1, 0, newTight, 0, 1, 0);
                else count += dfs(idx + 1, 1, newTight, digit, digit, 0);
            } else {
                int newHasZero;
                if (digit == 0) newHasZero = 1;
                else newHasZero= containsZero;
                count += dfs(idx + 1, 1, newTight, digitSum + digit, (digit == 0 ? 0 : digitProd * digit), newHasZero);
            }
        }

        dp[state] = count;
        return count;
    }

    ll count(ll X) {
        dp.clear();
        numStr = to_string(X);
        return dfs(0, 0, 1, 0, 1, 0);
    }

    int beautifulNumbers(int l, int r) {
        return count(r) - (l > 1 ? count(l - 1) : 0);
    }
};

