//      
#include <bits/stdc++.h>
#include <iostream>
#include <vector>
using namespace std;
#define rep(i,a,b) for (int i = (a); i < (b); i++)

#define ll long long
#define ar array
#define pb push_back

struct State {
    int pos, tight, started, hasZero, sum;
    ll prod;
    bool operator==(const State &o) const {
        return pos==o.pos && tight==o.tight && started==o.started && hasZero==o.hasZero && sum==o.sum && prod==o.prod;
    }
};

struct StateHash {
    size_t operator()(const State &st) const {
        size_t res = 17;
        res = res * 31 + hash<int>()(st.pos);
        res = res * 31 + hash<int>()(st.tight);
        res = res * 31 + hash<int>()(st.started);
        res = res * 31 + hash<int>()(st.hasZero);
        res = res * 31 + hash<int>()(st.sum);
        res = res * 31 + hash<ll>()(st.prod);
        return res;
    }
};

unordered_map<State, int, StateHash> dpMemo;
vector<int> ds;

int dp(int pos, int tight, int started, int hasZero, int sum, ll prod) {
    if(pos == ds.size()){
        if(!started) return 0;
        if(hasZero) return 1;
        return (prod % sum == 0) ? 1 : 0;
    }
    State st {pos, tight, started, hasZero, sum, prod};
    if(dpMemo.count(st)) return dpMemo[st];
    int res = 0;
    int limit = tight ? ds[pos] : 9;
    for (int d = 0; d <= limit; d++){
        int ntight = tight && (d == limit);
        int nstarted = started || (d > 0);
        int nhasZero = hasZero;
        int nsum = sum;
        ll nprod = prod;
        if(nstarted){
            nsum += d;
            if(d == 0)
                nhasZero = 1;
            else
                nprod *= d;
        }
        res += dp(pos + 1, ntight, nstarted, nhasZero, nsum, nprod);
    }
    dpMemo[st] = res;
    return res;
}

int countBeautiful(int x) {
    ds.clear();
