class Solution {
public:
    int beautifulNumbers(int l, int r) {
        const int threshold = 1000000;
        if(r - l <= threshold){
            int ans = 0;
            rep(num, l, r + 1) {
                int sum = 0, prod = 1;
                bool hasZero = false;
                for (int x = num; x; x /= 10) {
                    int d = x % 10;
                    if(d == 0) { hasZero = true; break; }
                    sum += d; prod *= d;
                }
                if(hasZero)
                    ans++;
                else if(sum && prod % sum == 0)
                    ans++;
            }
            return ans;
        } else {
            return countBeautiful(r) - countBeautiful(l - 1);
        }
    }
};

