class Solution {
  int beautifulNumbers(int l, int r) {
    String sn = "";
    Map<int, int> kq = {};
    int rc(int po, int ti, int st, int hz, int su, int e2, int e3, int e5, int e7) {
      if (po == sn.length) {
        if (st == 0) return 0;
        if (hz == 1) return 1;
        int pr = 1;
        for (int i = 0; i < e2; i++) pr *= 2;
        for (int i = 0; i < e3; i++) pr *= 3;
        for (int i = 0; i < e5; i++) pr *= 5;
        for (int i = 0; i < e7; i++) pr *= 7;
        return (su != 0 && pr % su == 0) ? 1 : 0;
      }
      int ky = po;
      ky |= (ti << 4);
      ky |= (st << 5);
      ky |= (hz << 6);
      ky |= (su << 7);
      ky |= (e2 << 14);
      ky |= (e3 << 20);
      ky |= (e5 << 26);
      ky |= (e7 << 31);
      if (kq.containsKey(ky)) return kq[ky]!;
      int rs = 0;
      int li = (ti == 1) ? int.parse(sn[po]) : 9;
      for (int dg = 0; dg <= li; dg++) {
        int nt = (ti == 1 && dg == li) ? 1 : 0;
        if (st == 0) {
          if (dg == 0) {
            rs += rc(po + 1, nt, 0, 0, 0, 0, 0, 0, 0);
          } else {
            int ns = dg;
            int a2 = 0, a3 = 0, a5 = 0, a7 = 0;
            int tp = dg;
            while (tp % 2 == 0) { a2++; tp ~/= 2; }
            tp = dg;
            while (tp % 3 == 0) { a3++; tp ~/= 3; }
            tp = dg;
            while (tp % 5 == 0) { a5++; tp ~/= 5; }
            tp = dg;
            while (tp % 7 == 0) { a7++; tp ~/= 7; }
            rs += rc(po + 1, nt, 1, 0, ns, a2, a3, a5, a7);
          }
        } else {
          if (hz == 1) {
            int ns = su + dg;
            rs += rc(po + 1, nt, 1, 1, ns, 0, 0, 0, 0);
          } else {
            if (dg == 0) {
              int ns = su;
              rs += rc(po + 1, nt, 1, 1, ns, 0, 0, 0, 0);
            } else {
              int ns = su + dg;
              int a2 = e2, a3 = e3, a5 = e5, a7 = e7;
              int tp = dg;
              while (tp % 2 == 0) { a2++; tp ~/= 2; }
              tp = dg;
              while (tp % 3 == 0) { a3++; tp ~/= 3; }
              tp = dg;
              while (tp % 5 == 0) { a5++; tp ~/= 5; }
              tp = dg;
              while (tp % 7 == 0) { a7++; tp ~/= 7; }
              rs += rc(po + 1, nt, 1, 0, ns, a2, a3, a5, a7);
            }
          }
        }
      }
      kq[ky] = rs;
      return rs;
    }
    int ct(int x) {
      if (x < 1) return 0;
      sn = x.toString();
      kq.clear();
      return rc(0, 1, 0, 0, 0, 0, 0, 0, 0);
    }
    return ct(r) - ct(l - 1);
  }
}

