typedef long long ll;
struct State {
    int pos, tight, started, sum, hasZero, e2, e3, e5, e7;
    bool operator==(const State &o) const {
        return pos==o.pos && tight==o.tight && started==o.started && sum==o.sum && hasZero==o.hasZero && e2==o.e2 && e3==o.e3 && e5==o.e5 && e7==o.e7;
    }
};
struct StateHash {
    size_t operator()(const State &st) const {
        size_t res = 17;
        res = res*31 + hash<int>()(st.pos);
        res = res*31 + hash<int>()(st.tight);
        res = res*31 + hash<int>()(st.started);
        res = res*31 + hash<int>()(st.sum);
        res = res*31 + hash<int>()(st.hasZero);
        res = res*31 + hash<int>()(st.e2);
        res = res*31 + hash<int>()(st.e3);
        res = res*31 + hash<int>()(st.e5);
        res = res*31 + hash<int>()(st.e7);
        return res;
    }
};
string num;
unordered_map<State,ll,StateHash> memo;
ll dfs(int pos, int tight, int started, int sum, int hasZero, int e2, int e3, int e5, int e7) {
    if(pos==num.size()){
        if(!started) return 0;
        if(hasZero) return 1;
        if(sum==0) return 0;
        ll prod = 1;
        for(int i=0;i<e2;i++) prod*=2;
        for(int i=0;i<e3;i++) prod*=3;
        for(int i=0;i<e5;i++) prod*=5;
        for(int i=0;i<e7;i++) prod*=7;
        return (prod % sum == 0) ? 1 : 0;
    }
    State st{pos,tight,started,sum,hasZero,e2,e3,e5,e7};
    if(memo.count(st)) return memo[st];
    ll res = 0;
    int up = tight ? num[pos]-'0' : 9;
    for(int d=0; d<=up; d++){
        int ntight = (tight && d==up);
        int nstarted = started || (d>0);
        int nsum = sum;
        int nhasZero = hasZero;
        int ne2 = e2, ne3 = e3, ne5 = e5, ne7 = e7;
        if(nstarted){
            nsum += d;
            if(d==0) nhasZero = 1;
            else {
                if(d==2) ne2++;
                else if(d==3) ne3++;
                else if(d==4) ne2 += 2;
                else if(d==5) ne5++;
                else if(d==6){ ne2++; ne3++; }
                else if(d==7) ne7++;
                else if(d==8) ne2 += 3;
                else if(d==9) ne3 += 2;
            }
        }
        res += dfs(pos+1, ntight, nstarted, nsum, nhasZero, ne2, ne3, ne5, ne7);
    }
    memo[st]=res;
    return res;
}
ll solve(ll x){
    num = to_string(x);
    memo.clear();
    return dfs(0,1,0,0,0,0,0,0,0);
}
class Solution {
public:
    int beautifulNumbers(int l, int r) {
        return solve(r) - solve(l-1);
    }
};

