class Solution {
    private static final int[] mem = {0,9,15,309,3675,43462,486887,5311174,57110375,607393752,2092560258};

    public int beautifulNumbers(int l, int r) {
        return count(r) - count(l - 1);
    }

    private int count(int n) {
        if (n == 0) return 0;
        String num = String.valueOf(n);
        int max = num.length() * 9;
        int res = 0;
        for (int i = 0; i < num.length(); ++i) {
            res += mem[i];
        }
        for (int i = 1; i <= max; ++i) {
            Integer[][][][] dp = new Integer[num.length()][2][max + 1][max + 1];
            res += helper(dp, 0, 0, 1, i, i, num);
        }
        return res;
    }

    private int helper(Integer[][][][] dp, int pos, int restrict, int mul, int remain, int target, String num) {
        if (pos == dp.length) {
            if (remain != 0) {
                return 0;
            } else {
                return mul % target == 0 ? 1 : 0;
            }
        } else if (dp[pos][restrict][remain][mul] == null) {
            int st = 0, ed = Math.min(9, remain), res = 0;
            if (pos == 0) st = 1;
            if (restrict == 0) {
                ed = Math.min(ed, num.charAt(pos) - '0');
            }
            for (int i = st; i <= ed; ++i) {
                if (restrict == 0 && i == num.charAt(pos) - '0') {
                    res += helper(dp, pos + 1, 0,mul * i % target, remain - i, target, num);
                } else {
                    res += helper(dp, pos + 1, 1,mul * i % target, remain - i, target, num);
                }
            }
            dp[pos][restrict][remain][mul] = res;
        }
        return dp[pos][restrict][remain][mul];
    }
}

