typedef long long ll;

struct ddState {
    int index, isTight, hasf, ddSum, con0, cnt2, cnt3, cnt5, cnt7;

    bool operator==(const ddState &other) const {
        return index == other.index && isTight == other.isTight && hasf == other.hasf && 
               ddSum == other.ddSum && con0 == other.con0 && cnt2 == other.cnt2 && 
               cnt3 == other.cnt3 && cnt5 == other.cnt5 && cnt7 == other.cnt7;
    }

    bool operator<(const ddState &other) const {
        if (index != other.index) return index < other.index;
        if (isTight != other.isTight) return isTight < other.isTight;
        if (hasf != other.hasf) return hasf < other.hasf;
        if (ddSum != other.ddSum) return ddSum < other.ddSum;
        if (con0 != other.con0) return con0 < other.con0;
        if (cnt2 != other.cnt2) return cnt2 < other.cnt2;
        if (cnt3 != other.cnt3) return cnt3 < other.cnt3;
        if (cnt5 != other.cnt5) return cnt5 < other.cnt5;
        return cnt7 < other.cnt7;
    }
};

string numStr;
map<ddState, ll> hsh;

ll cntBeautiful(int index, int isTight, int hasf, int ddSum, int con0, int cnt2, int cnt3, int cnt5, int cnt7) {
    if (index == numStr.size()) {
        if (!hasf) return 0;
        if (con0) return 1;
        if (ddSum == 0) return 0;
        ll product = 1;
        for (int i = 0; i < cnt2; i++) product *= 2;
        for (int i = 0; i < cnt3; i++) product *= 3;
        for (int i = 0; i < cnt5; i++) product *= 5;
        for (int i = 0; i < cnt7; i++) product *= 7;
        return (product % ddSum == 0) ? 1 : 0;
    }
    ddState state{index, isTight, hasf, ddSum, con0, cnt2, cnt3, cnt5, cnt7};
    if (hsh.count(state)) return hsh[state];
    ll result = 0;
    int upperLimit = isTight ? numStr[index] - '0' : 9;
    for (int dd = 0; dd <= upperLimit; dd++) {
        int nextTight = (isTight && dd == upperLimit);
        int nextStarted = hasf || (dd > 0);
        int nextSum = ddSum;
        int nextcon0 = con0;
        int nextcnt2 = cnt2, nextcnt3 = cnt3, nextcnt5 = cnt5, nextcnt7 = cnt7;
        if (nextStarted) {
            nextSum += dd;
            if (dd == 0) nextcon0 = 1;
            else {
                if (dd == 2) nextcnt2++;
                else if (dd == 3) nextcnt3++;
                else if (dd == 4) nextcnt2 += 2;
                else if (dd == 5) nextcnt5++;
                else if (dd == 6) { nextcnt2++; nextcnt3++; }
                else if (dd == 7) nextcnt7++;
                else if (dd == 8) nextcnt2 += 3;
                else if (dd == 9) nextcnt3 += 2;
            }
        }
        result += cntBeautiful(index + 1, nextTight, nextStarted, nextSum, nextcon0, nextcnt2, nextcnt3, nextcnt5, nextcnt7);
    }
    hsh[state] = result;
    return result;
}

ll calBeautiful(ll x) {
    numStr = to_string(x);
    hsh.clear();
    return cntBeautiful(0, 1, 0, 0, 0, 0, 0, 0, 0);
}

class Solution {
public:
    int beautifulNumbers(int left, int right) {
        return calBeautiful(right) - calBeautiful(left - 1);
    }
};

