class Solution {
public:
    int beautifulNumbers(int l, int r) {
        auto countBeautiful = [&](int n) -> long long {
            if(n < 1) return 0;
            string s = to_string(n);
            int L = s.size();
            vector<int> digits(L);
            for (int i = 0; i < L; i++) {
                digits[i] = s[i] - '0';
            }

            unordered_map<long long, long long> dpMemo;
            function<long long(int, int, int, int, int, int)> dp =
            [&](int pos, int tight, int leading, int hasZero, int sum, int prod) -> long long {
                if (pos == L) {
                    if (leading) return 0LL;
                    if (hasZero) return 1LL;
                    return (sum>0 && (prod%sum==0)) ? 1LL : 0LL;
                }
                long long state = encodeState(pos, tight, leading, hasZero, sum, prod);
                if (dpMemo.count(state)) return dpMemo[state];

                long long res = 0;
                int limit = (tight ? digits[pos] : 9);
                for (int d=0; d <= limit; d++) {
                    int newTight = (tight && (d == limit)) ? 1 : 0;
                    if (leading && d == 0) {
                        res += dp(pos + 1, newTight, 1, hasZero, sum, prod);
                    } else {
                        int newLeading = 0;
                        int newHasZero = hasZero;
                        int newSum = sum + d;
                        int newProd;
                        if (d==0) {
                            newHasZero = 1;
                            newProd = 0;
                        } else {
                            newProd = (hasZero ? 0 : prod * d);
                        }
                        res += dp(pos+1, newTight, newLeading, newHasZero, newSum, newProd);
                    }
                }
                dpMemo[state] = res;
                return res;
            };
            return dp(0,1,1,0,0,1);
        };
        long long countR = countBeautiful(r);
        long long countL = countBeautiful(l-1);
        long long ans = countR - countL;
        return (int)ans;
    }
private:
    long long encodeState(int pos, int tight, int leading, int hasZero, int sum, int prod) {
        long long state = pos;
        state |= ((long long)tight << 4);
        state |= ((long long)leading << 5);
        state |= ((long long)hasZero << 6);
        state |= ((long long)sum << 7);
        state |= ((long long)prod << 14);
        return state;
    }
};

