class Solution {
private:
    // DP
    long long cal(int pos, int tight, int started, int hasZero, int sum, int prod) {
        long long key = pos;
        key = key * 2LL + tight;     // posflag
        key = key * 2LL + started;   // started
        key = key * 2LL + hasZero;   // hasZero
        key = key * 100LL + sum;     // sum[0,81]100
        key = key * 1000000000LL + prod; // prod < 1e9
        return key;
    }
    string s;
    int n;
    unordered_map<long long, long long> memo;
    // pos, flag1,started
    // hasZero0true0,sum,prod
    long long dfs(int pos, int flag, int started, int hasZero, int sum, int prod) {
        if (pos == n) {
            if (!started) return 0; // 0
            if (hasZero) return 1;  // 0
            return (prod % sum == 0) ? 1LL : 0LL;
        }
        long long state = cal(pos, flag, started, hasZero, sum, prod);
        if(memo.contains(state)) return memo[state];
        long long res = 0;
        int limit = flag ? (s[pos] - '0') : 9;
        for (int d = 0; d <= limit; d++) { // 
            int nTight = (flag && (d == limit)) ? 1 : 0;
            if (!started) {
                // 
                if (d == 0) {
                    // sumprod
                    res += dfs(pos + 1, nTight, 0, 0, 0, 1);
                } else {
                    // , sumprod
                    res += dfs(pos + 1, nTight, 1, 0, d, d);
                }
            } else {
                int newSum = sum + d;
                int newProd;
                int newHasZero = hasZero;
                if (hasZero) newProd = 0; // 0
                else {
                    if (d == 0) {
                        newHasZero = 1;
                        newProd = 0;
                    } else {
                        newProd = prod * d;
                    }
                }
                res += dfs(pos + 1, nTight, 1, newHasZero, newSum, newProd);
            }
        }
        memo[state] = res;
        return res;
    }
    long long countBeautiful(long long x) {
        if (x < 1) return 0;
        s = to_string(x);
        n = s.size();
        memo.clear();
        return dfs(0, 1, 0, 0, 0, 1);
    }
public:
    int beautifulNumbers(int l, int r) {
        return countBeautiful(r) - countBeautiful(l - 1);
    }
};

