                       for(int add=0; add<=9; add++){
                           if(j==2){
                               dp[i+1][j][sum+add][(du*add)%b] += dp[i][j][sum][du];
                           }
                           else if(j==1){
                               int st=0;
                               if(add > a[i]-'0'){
                                   st=2;
                               }
                               else if(add == a[i]-'0'){
                                   st=1;
                               }
                               else st=0;
                               dp[i+1][st][sum+add][(du*add)%b] += dp[i][j][sum][du];
                           }
                           else if(j==0){
                               dp[i+1][0][sum+add][(du*add)%b] +=dp[i][j][sum][du];
                           }
                       } 
                    }
                }
            }
        }
        int dem=0;
        for(int i=1; i<=a.size(); i++){
            dem += dp[i][0][b][0] + dp[i][1][b][0] + dp[i][2][b][0];
        }
        dem -= dp[a.size()][2][b][0];
        return dem;
    }
    int beautifulNumbers(int l, int r) {
        int ans = 0;
        if(r==1000000000){
            ans=1;
            r--;
        }
        if(r-l+1<=1000000){
            for(int i=l; i<=r; i++){
                int sum = 0;
                long long tich = 1;
                int a = i;
                while(a>0){
                    sum+=a%10;
                    tich=tich*(a%10);
                    a/=10;
                }
                if(tich%sum==0) ans+=1;
            }
            return ans;

        }
        for(int sum=1; sum<=81; sum++){
            ans += calc(r,sum) - calc(l-1,sum);
        }
        return ans;
    }
};

