class Solution {
public:
    int calcAll(int ind, bool tight, bool started, string& num, vector<vector<vector<int>>>& dp) {
        if (ind == num.size())
            return started;
        if (dp[tight][started][ind] != -1)
            return dp[tight][started][ind];
        int res = 0, lim = tight ? (num[ind] - '0') : 9;
        for (int i = 0; i <= lim; i++) {
            bool newTight = (tight && (i == lim));
            if (i)
                res += calcAll(ind + 1, newTight, 1, num, dp);
            else if (!started)
                res += calcAll(ind + 1, newTight, 0, num, dp);
        }
        return dp[tight][started][ind] = res;
    }
    int calcBeauty(int ind, bool tight, bool started, int sum, int prod, string& num,
             map<vector<int>, int>& dp) {
        if (ind == num.size())
            return (started && (prod % sum == 0));
        vector<int> key = {ind, tight, started, sum, prod};
        if (dp.find(key) != dp.end())
            return dp[key];
        int res = 0, lim = tight ? (num[ind] - '0') : 9;
        for (int i = 0; i <= lim; i++) {
            int newTight = (tight && (i == lim));
            if (i)
                res += calcBeauty(ind + 1, newTight, 1, sum + i, prod * i, num, dp);
            else if (!started)
                res += calcBeauty(ind + 1, newTight, 0, sum, prod, num, dp);
        }
        return dp[key] = res;
    }
    int calc(int x) {
        if (!x)
            return 0;
        string num = to_string(x);
        map<vector<int>, int> dp;
        vector<vector<vector<int>>> dp1(2, vector<vector<int>>(2, vector<int>(num.size(), -1)));
        return x - calcAll(0, 1, 0, num, dp1) + calcBeauty(0, 1, 0, 0, 1, num, dp);
    }
    int beautifulNumbers(int l, int r) { 
        return calc(r) - calc(l - 1); 
    }
};

