class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        num2=str(r)
        num1=str(l).zfill(len(num2))
        n=len(num2)
        MI=[num1,'0'*n]
        MX=[num2,'9'*n]
        ans=0
        @cache
        def dp(i, mi_, mx_,leading_zero=True, prod=1,sm=0):
            mi=MI[mi_]
            mx=MX[mx_]
            if i == n:
                return prod%sm==0
            cur_min=int(mi[i])
            cur_max=int(mx[i])
            ans = 0
            for dig in range(cur_min, cur_max + 1):
                nlead=False
                if leading_zero==True:
                    if dig==0:
                        nlead=True
                    else:
                        nlead=False
                next_mi_ = mi_!=0 or dig != cur_min
                next_mx_ = mx_!=0 or dig != cur_max
                ans += dp(i + 1, next_mi_, next_mx_, nlead, (prod*dig) if not nlead else prod, sm+dig)
            return ans
        return dp(0, 0, 0)

