class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def count_up_to(n: int) -> int:
            digits = list(map(int, str(n)))
            memo = {}
            return self.dp(0, True, True, 0, 1, digits, memo)

        return count_up_to(r) - count_up_to(l - 1)

    def dp(self, pos, tight, leading_zero, summ, prod, target_digits, memo):
        if pos == len(target_digits):
            return 1 if summ > 0 and prod % summ == 0 else 0

        if (pos, tight, leading_zero, summ, prod) in memo:
            return memo[(pos, tight, leading_zero, summ, prod)]

        max_val = target_digits[pos] if tight else 9
        res = 0

        for val in range(max_val + 1):
            next_tight = tight and (val == max_val)
            next_leading_zero = leading_zero and (val == 0)
            next_summ = summ + val if not next_leading_zero else 0
            next_prod = (prod * val) if not next_leading_zero else 1
            res += self.dp(pos + 1, next_tight, next_leading_zero, next_summ, next_prod, target_digits, memo)

        memo[(pos, tight, leading_zero, summ, prod)] = res
        return res

