class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def count(x):
            if x<1:
                return 0
            s=str(x)
            d=[int(ch) for ch in s]
            @lru_cache(maxsize=None)
            def dp(pos,ti,st,s,p):
                if pos== len(d):
                    if not st:
                        return 0
                    return 1 if (s!=0 and p%s==0) else 0
                lt=d[pos] if ti else 9
                w=0
                for i in range(0,lt+1):
                    nt=1 if (ti==1 and i==lt) else 0
                    if not st:
                        if i==0:
                            w+=dp(pos+1,nt,0,0,1)
                        else:
                            w+=dp(pos+1,nt,1,i,i)
                    else:
                        w+=dp(pos+1,nt,1,s+i,p*i)
                return w
            return dp(0,1,0,0,1)
        return count(r)-count(l-1)

