using i64 = long long;

constexpr int N = 10;
unordered_map<int, int> f[N][2][2][82];
string s;
int n;

int dp(int i, int is_leading, int is_tight, int sum, int prod) {
    if (f[i][is_leading][is_tight][sum].contains(prod)) {
        return f[i][is_leading][is_tight][sum][prod];
    }
    int& ans = f[i][is_leading][is_tight][sum][prod];
    if (i == n) {
        if (!is_leading && sum > 0 && prod % sum == 0) {
            ans++;
        }
        return ans;
    }

    int d = s[i] - '0';
    int end = (is_tight ? d : 9);
    for (int cand = 0; cand <= end; cand++) {
        int tight = int(is_tight && cand == d);
        if (is_leading) {
            if (cand != 0) {
                ans += dp(i + 1, 0, tight, sum + cand, prod * cand);
            } else {
                ans += dp(i + 1, is_leading, tight, sum, prod);
            }
        } else {
            ans += dp(i + 1, 0, tight, sum + cand, prod * cand);
        }
    }
    return ans;
}

int solve(int x) {
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < 2; j++) {
            for (int k = 0; k < 2; k++) {
                for (int l = 0; l < 82; l++) {
                    f[i][j][k][l].clear();
                }
            }
        }
    }
    s = to_string(x);
    n = s.size();
    return dp(0, 1, 1, 0, 1);
}

class Solution {
public:
    int good(int x) {
        int p = 1;
        int s = 0;
        while (x) {
            int d = (x % 10);
            p *= d;
            s += d;
            x /= 10;
        }
        return p % s == 0;
    }

    int beautifulNumbers(int l, int r) {
        int R = solve(r);
        int L = solve(l);
        return R - L + good(l);
    }
};

