class Solution {
public:
    vector<vector<vector<long long>>> memoAll, memoNoZero;
    map<int, map<int, map<int, map<pair<int, int>, long long>>>> memoBea;

    long long dpAll(int pos, bool tight, bool started, const string &s) {
        if (pos == s.size())
            return started ? 1 : 0;  

        if (memoAll[pos][tight][started] != -1)
            return memoAll[pos][tight][started];

        long long ans = 0;
        int limit = tight ? s[pos] - '0' : 9;
        for (int d = 0; d <= limit; d++) {
            bool ntight = tight && (d == limit);
            bool nstarted = started || (d != 0);
            ans += dpAll(pos + 1, ntight, nstarted, s);
        }
        memoAll[pos][tight][started] = ans;
        return ans;
    }

    long long dpNoZero(int pos, bool tight, bool started, const string &s) {
        if (pos == s.size())
            return started ? 1 : 0;

        if (memoNoZero[pos][tight][started] != -1)
            return memoNoZero[pos][tight][started];

        long long ans = 0;
        int limit = tight ? s[pos] - '0' : 9;
        for (int d = 0; d <= limit; d++) {
            if (started && d == 0) continue; 

            bool nstarted = started || (d != 0);
            bool ntight = tight && (d == limit);
            ans += dpNoZero(pos + 1, ntight, nstarted, s);
        }
        memoNoZero[pos][tight][started] = ans;
        return ans;
    }

    long long dpBeautifulNoZero(int pos, bool tight, bool started, int sum, int prod, const string &s) {
        if (pos == s.size()) {
            return (started && sum > 0 && (prod % sum == 0)) ? 1LL : 0LL;
        }

        pair<int, int> key = make_pair(sum, prod);
        if (memoBea[pos][tight][started].count(key))
            return memoBea[pos][tight][started][key];

        long long ans = 0;
        int limit = tight ? s[pos] - '0' : 9;
        for (int d = 0; d <= limit; d++) {
            bool ntight = tight && (d == limit);
            bool nstarted = started || (d != 0);
            if (nstarted) {
                if (d == 0) continue;
                ans += dpBeautifulNoZero(pos + 1, ntight, nstarted, sum + d, prod * d, s);
            } else {
                ans += dpBeautifulNoZero(pos + 1, ntight, nstarted, sum, prod, s);
            }
        }
        memoBea[pos][tight][started][key] = ans;
        return ans;
    }

    long long f(long long X) {
        string s = to_string(X);
        int n = s.size();

        memoAll = vector<vector<vector<long long>>>(n, vector<vector<long long>>(2, vector<long long>(2, -1)));
        memoNoZero = vector<vector<vector<long long>>>(n, vector<vector<long long>>(2, vector<long long>(2, -1)));
        memoBea.clear(); 

        long long total = dpAll(0, true, false, s);
        long long noZero = dpNoZero(0, true, false, s);
        long long withZero = total - noZero;
        long long beautifulNoZero = dpBeautifulNoZero(0, true, false, 0, 1, s);

        return withZero + beautifulNoZero;
    }

    int beautifulNumbers(int l, int r) {
        return f(r) - f(l - 1);
    }
};

