#include <string>
#include <unordered_map>
using namespace std;

class Solution {
public:
    int beautifulNumbers(int l, int r) {
        return (int)(countBeautiful(to_string(r)) - countBeautiful(to_string(l - 1)));
    }

private:
    unordered_map<string, long long> memo;

    long long dp(int pos, int tight, int started, int sum, long long prod, const string &s) {
        if (pos == s.size()) {
            if (!started)
                return 0;
            return (prod == 0 || (sum > 0 && prod % sum == 0)) ? 1LL : 0LL;
        }


        string key = to_string(pos) + "|" + to_string(tight) + "|" + to_string(started) + "|" 
                     + to_string(sum) + "|" + to_string(prod);
        if (memo.find(key) != memo.end())
            return memo[key];

        long long ans = 0;
        int limit = tight ? (s[pos] - '0') : 9;
        for (int d = 0; d <= limit; d++) {
            int newTight = (tight && (d == limit)) ? 1 : 0;
            int newStarted = started;
            int newSum = sum;
            long long newProd = prod;

            if (!started) {
                if (d == 0) {
                    newStarted = 0;
                    newSum = 0;
                    newProd = 1; 
                } else {
                    newStarted = 1;
                    newSum = d;
                    newProd = d;
                }
            } else {
                newSum = sum + d;
                if (newProd == 0 || d == 0)
                    newProd = 0;
                else
                    newProd = prod * d;
            }
            ans += dp(pos + 1, newTight, newStarted, newSum, newProd, s);
        }

        memo[key] = ans;
        return ans;
    }
    long long countBeautiful(const string &num) {
        memo.clear();
        return dp(0, 1, 0, 0, 1, num);
    }
};

