#define rep(i,a,b) for(int i=(a);i<(b);i++)
#define sz(a) (int)(a).size()
#define all(a) begin(a),end(a)
typedef vector<int> vi;
class Solution {
public:
    int sum(int n) {
        if(n==0)return 0;
        int ans=0;
        string s=to_string(n);
        {
            if(count(all(s),'0'))ans++;
            rep(d,2,sz(s))ans+=9*round(pow(10,d-1)-pow(9,d-1));
            vector<int>dp(sz(s)),ep(sz(s)),fp(sz(s)),gp(sz(s));
            rep(d,1,s[0]-'0')dp[0]++;
            ep[0]=1;
            rep(i,1,sz(s)){
                rep(d,0,10){
                    if(d==0)fp[i]+=dp[i-1]+fp[i-1];
                    else dp[i]+=dp[i-1],fp[i]+=fp[i-1];
                    if(d<s[i]-'0'){
                        if(d==0)fp[i]+=ep[i-1]+gp[i-1];
                        else dp[i]+=ep[i-1],fp[i]+=gp[i-1];
                    }
                    else if(d==s[i]-'0'){
                        if(d==0)gp[i]+=ep[i-1]+gp[i-1];
                        else ep[i]+=ep[i-1],gp[i]+=gp[i-1];
                    }
                }
            }
            ans+=fp.back();
        }
        int prod=1,sum=0;
        string a;
        vector<int>f(12,1);
        rep(i,1,12)f[i]=i*f[i-1];
        function<void(int)>dfs=[&](int i){
            if(i==10){
                if(sum&&prod%sum==0){
                    if(sz(a)<sz(s)){
                        int q=f[sz(a)];
                        rep(i,1,10){
                            int c=count(all(a),'0'+i);
                            q/=f[c];
                        }
                        ans+=q;
                    }
                    else if(sz(a)==sz(s)){
                        vector<int>dp(1<<sz(s)),ep(1<<sz(s));
                        dp[0]=1;
                        rep(i,0,1<<sz(s))rep(j,0,sz(s))if((i>>j)%2==0){
                            if(j&&(i>>(j-1))%2==0&&a[j]==a[j-1])continue;
                            ep[i+(1<<j)]+=ep[i];
                            int c=__builtin_popcount(i);
                            if(s[c]>a[j])ep[i+(1<<j)]+=dp[i];
                            else if(s[c]==a[j])dp[i+(1<<j)]+=dp[i];
                        }
                        ans+=ep.back();
                        {
                            auto t=s;
                            sort(all(t));
                            if(a==t)ans++;
                        }
                    }
                }
                return;
            }
            int d=sz(a),_prod=prod;
            rep(_,0,10){
                dfs(i+1);
                if(sz(a)==sz(s))break;
                a+=(char)('0'+i);
                sum+=i;
                prod*=i;
            }
            sum-=i*(sz(a)-d);
            a.erase(a.begin()+d,a.end());
            prod=_prod;
        };
        dfs(1);
        return ans;
    }
    int beautifulNumbers(int l, int r) {
        return sum(r)-sum(l-1);
    }
};

