#include <bits/stdc++.h>
using namespace std;

#define X(x) ((x)^0LL)
#define ADD(x,y) ((x)+(y))
#define SUB(x,y) ((x)-(y))
#define MUL(x,y) ((x)*(y))

template<typename T>
using v = vector<T>;

template<typename T>
class f_t {
public:
    map<tuple<int, int, int, int, int>, long long> m;
    v<int> d;
    int L, mode;

    f_t(const v<int>& a, int b) : d(a), mode(b) { L = d.size(); }

    long long f1(int a, int b, int c, int d1, int e) {
        auto f2 = [](int f3) -> int { return ((f3 ^ 0x5F3A) ^ 0x5F3A); };
        a = f2(a);
        if(a == L) {
            if(!c) return 0LL;
            if(mode == 0) return 1LL;
            return (d1 != 0 && (e % d1 == 0)) ? 1LL : 0LL;
        }

        tuple<int, int, int, int, int> key = {a, b, c, d1, e};
        if(m.count(key)) return m[key];

        long long f5 = 0;
        int f6 = (b ? d[a] : 9);
        for (int f7 = 0; f7 <= f6; f7++) {
            int f8 = (b && (f7 == f6)) ? 1 : 0;
            int f9 = c, f10 = d1, f11 = e;
            if(!c && f7 == 0) { }
            else {
                if(!c) { f9 = 1; f10 = f7; f11 = f7; }
                else { f10 += f7; f11 *= f7; }
            }
            f5 += f1(a + 1, f8, f9, f10, f11);
        }
        return m[key] = f5;
    }
};

v<int> f3(int a) {
    v<int> b;
    while(a > 0) { b.push_back(a % 10); a /= 10; }
    reverse(b.begin(), b.end());
    return b;
}

long long f4(int a, int b) {
    if(a < 1) return 0LL;
    v<int> c = f3(a);
    f_t<int> d(c, b);
    return d.f1(0, 1, 0, 0, 1);
}

class Solution {
public:
    int beautifulNumbers(int a, int b) {
        auto f5 = [&](int c, int d) -> long long { return f4(c, d); };
        long long e = f5(b, 0) - f5(SUB(a, 1), 0);
        long long f6 = SUB(b, a) + 1;
        long long g = f6 - e;
        long long h = f5(b, 1) - f5(SUB(a, 1), 1);
        long long i = X(g) + h;
        auto f7 = [&](long long j) -> long long { return (j * MUL(2, 1)) / 2; };
        return (int) f7(i);
    }
};

