class Solution {
    public int beautifulNumbers(int l, int r) {
        int ans = 0;
        for (int i = 1; i < 100; i++) {
            ans += beautifulNumbers(l, r, i);
        }
        return ans;
    }

    private int beautifulNumbers(int l, int r, int i) {
        return countSpecialNumbers(r, i) - countSpecialNumbers(l - 1, i);
    }

    char[] s;
    int[][][] dp;

    public int countSpecialNumbers(int n, int v) {
        if (n == 0) {
            return 0;
        }
        this.s = String.valueOf(n).toCharArray();
        int m = this.s.length;
        dp = new int[m][v + 1][v + 1];
        for (int i = 0; i < m; i++) {
            for (int j = 0; j <= v; j++) {
                Arrays.fill(dp[i][j], -1);
            }
        }
        return f(0, v, v, true, false);
    }

    /**
     * i
     */
    int f(int i, int sum, int prod, boolean isLimit, boolean isNum) {
        if (i == s.length) {
            return isNum && sum == 0 && prod == 1 ? 1 : 0;
        }
        if (!isLimit && isNum && dp[i][sum][prod] >= 0) {
            return dp[i][sum][prod];
        }
        int res = 0;
        if (!isNum) {
            // 
            res = f(i + 1, sum, prod, false, false);
        }
        //  d
        for (int d = isNum ? 0 : 1, up = isLimit ? s[i] - '0' : 9; d <= up; d++) {
            if (sum >= d) {
                res += f(i + 1, sum - d, prod / gcd(prod, d), isLimit && d == up, true);
            }
        }
        if (!isLimit && isNum) {
            dp[i][sum][prod] = res;
        }
        return res;
    }

    private int gcd(int a, int b) {
        return b == 0 ? a : gcd(b, a % b);
    }
}

