class Solution:
    def __init__(self):
        self.memo = {}
        self.numString = ""

    def calculateProduct(self, powers):
        product = 1
        for base, exponent in powers.items():
            product *= base ** exponent
        return product

    def generateKey(self, position, tight, started, hasZero, digitSum, powers):
        return (position, tight, started, hasZero, digitSum, tuple(powers.items()))

    def recursiveCount(self, position, tight, started, hasZero, digitSum, powers):
        if position == len(self.numString):
            if not started:
                return 0
            if hasZero:
                return 1
            product = self.calculateProduct(powers)
            return 1 if digitSum != 0 and product % digitSum == 0 else 0

        key = self.generateKey(position, tight, started, hasZero, digitSum, powers)
        if key in self.memo:
            return self.memo[key]

        result = 0
        limit = int(self.numString[position]) if tight else 9

        for digit in range(0, limit + 1):
            newTight = tight and (digit == limit)
            newStarted = started or (digit != 0)
            newHasZero = hasZero or (newStarted and digit == 0)
            newDigitSum = digitSum + digit if newStarted else 0
            newPowers = powers.copy()

            if newStarted and digit > 0:
                for base in [2, 3, 5, 7]:
                    while digit % base == 0:
                        newPowers[base] += 1
                        digit //= base

            result += self.recursiveCount(position + 1, newTight, newStarted, newHasZero, newDigitSum, newPowers)

        self.memo[key] = result
        return result

    def countBeautiful(self, number):
        if number < 1:
            return 0
        self.numString = str(number)
        self.memo.clear()
        return self.recursiveCount(0, 1, 0, 0, 0, {2: 0, 3: 0, 5: 0, 7: 0})

    def beautifulNumbers(self, left, right):
        return self.countBeautiful(right) - self.countBeautiful(left - 1)

