import itertools
import functools

class Solution:
    def beautifulNumbers(self, lower: int, upper: int) -> int:
        return self.count_valid_numbers(upper) - self.count_valid_numbers(lower - 1)

    def get_digits(self, num: int):
        return list(map(int, str(num)))

    def init_dp(self):
        return {(0, 1, 0): {(0, 1): 1}}

    def update_dp(self, dp_table, index, max_digit):
        next_dp = {}
        for (pos, limit, nonzero), count_map in dp_table.items():
            highest_digit = max_digit if limit else 9

            for digit in range(highest_digit + 1):
                new_limit = int(limit and digit == highest_digit)
                new_nonzero = int(nonzero or digit > 0)
                key = (index + 1, new_limit, new_nonzero)
                temp_map = next_dp.setdefault(key, {})

                for (sum_val, prod_val), occurrences in count_map.items():
                    updated_sum, updated_product = (sum_val + digit, prod_val * digit) if new_nonzero else (0, 1)
                    temp_map[updated_sum, updated_product] = temp_map.get((updated_sum, updated_product), 0) + occurrences

        return next_dp

    def count_valid_numbers(self, num: int) -> int:
        if num < 1:
            return 0

        digits_list = self.get_digits(num)
        total_digits = len(digits_list)
        dp_table = self.init_dp()

        for idx, digit in enumerate(digits_list):
            dp_table = self.update_dp(dp_table, idx, digit)

        return sum(
            occurrences for (pos, _, nonzero), count_map in dp_table.items() if pos == total_digits and nonzero
            for (sum_val, prod_val), occurrences in count_map.items()
            if sum_val and prod_val % sum_val == 0
        )

