from functools import lru_cache

class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def count_beautiful(n):
            if n == 0:
                return 0
            digits = list(map(int, str(n)))

            @lru_cache(None)
            def dp(pos, tight, prod, summ, leading_zero):
                if pos == len(digits):
                    return int(summ > 0 and prod % summ == 0)

                limit = digits[pos] if tight else 9
                res = 0

                for d in range(0, limit + 1):
                    new_prod = prod if leading_zero and d == 0 else prod * d
                    new_summ = summ + d
                    res += dp(pos + 1, tight and (d == limit), new_prod, new_summ, leading_zero and d == 0)

                return res

            return dp(0, True, 1, 0, True)

        return count_beautiful(r) - count_beautiful(l - 1)

