class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def count_A(n):
            if n == 0:
                return 0
            return n - count_non_zero(n)

        def count_non_zero(n):
            s = list(map(int, str(n)))
            m = len(s)

            from functools import lru_cache

            @lru_cache(maxsize=None)
            def dfs(pos, tight, leading):
                if pos == m:
                    return 0 if leading else 1
                max_d = s[pos] if tight else 9
                total = 0
                for d in range(0, max_d + 1):
                    new_tight = tight and (d == max_d)
                    new_leading = leading and (d == 0)
                    if new_leading:
                        # Continue leading zeros
                        total += dfs(pos + 1, new_tight, new_leading)
                    else:
                        if d == 0:
                            continue  # Skip zeros in non-leading positions
                        total += dfs(pos + 1, new_tight, False)
                return total

            return dfs(0, True, True)

        def count_B(n):
            if n == 0:
                return 0
            s = list(map(int, str(n)))
            m = len(s)

            from functools import lru_cache

            @lru_cache(maxsize=None)
            def dfs(pos, tight, leading_zero, sum_so_far, prod_so_far):
                if pos == m:
                    if leading_zero:
                        return 0
                    if sum_so_far == 0:
                        return 0
                    return 1 if (prod_so_far % sum_so_far) == 0 else 0
                max_d = s[pos] if tight else 9
                total = 0
                for d in range(0, max_d + 1):
                    new_tight = tight and (d == max_d)
                    new_leading_zero = leading_zero and (d == 0)
                    new_sum = sum_so_far
                    new_prod = prod_so_far
                    if not new_leading_zero:
                        if d == 0:
                            continue  # Skip zeros once leading_zero is False
                        new_sum += d
                        new_prod *= d
                    total += dfs(pos + 1, new_tight, new_leading_zero, new_sum, new_prod)
                return total

            return dfs(0, True, True, 0, 1)

        a_r = count_A(r)
        a_l_1 = count_A(l - 1) if l > 1 else 0
        b_r = count_B(r)
        b_l_1 = count_B(l - 1) if l > 1 else 0

        return (a_r - a_l_1) + (b_r - b_l_1)

