class Solution {
public:
    unordered_map<long long, int> dp;
    int dfs(vector<int>& digits, long long index, long long bound, long long started, long long digitSum, long long digitProduct) {
        // Base case
        // cout << index << endl;
        if (index == digits.size()) {
            if (!started) return 0;
            return (digitProduct == 0 || digitProduct % digitSum == 0) ? 1 : 0;
        }

        // Memoization
        long long key = (index << 60) + (bound << 59) + (started << 58) + (digitSum << 26) + digitProduct;
        if (dp.find(key) != dp.end()) return dp[key];

        int result = 0;
        int end = bound ? digits[index] : 9;
        // cout << "End: " << end << endl;
        for (int i = 0; i <= end; ++i) {
            int nextBound = (bound && (i == end)) ? 1 : 0;
            int nextStarted = started;
            int nextDigitSum = digitSum;
            int nextDigitProduct = digitProduct;

            if (!started) {
                if (i == 0) {
                    nextStarted = 0;
                    nextDigitSum = 0;
                    nextDigitProduct = 1;
                } else {
                    nextStarted = 1;
                    nextDigitSum = i;
                    nextDigitProduct = i;
                }
            } else {
                nextDigitSum = digitSum + i;
                nextDigitProduct = (i == 0 || digitProduct == 0) ? 0 : digitProduct * i;
            }

            result += dfs(digits, index + 1, nextBound, nextStarted, nextDigitSum, nextDigitProduct);
        }
        dp[key] = result;
        return result;
    }

    int count(int end) {
        string str = to_string(end);
        // cout << str << endl;
        vector<int> digits(str.size());
        for (int i = 0; i < str.size(); ++i) {
            digits[i] = str[i] - '0';   
        }
        dp.clear();
        return dfs(digits, 0, 1, 0, 0, 1);
    }

    int beautifulNumbers(int l, int r) {
        // cout << count(r) << endl;
        return count(r) - count(l - 1);
    }
};

